/*-------------------------------------------------------------------------------------------------+
>>  dataset 에서 OnRowPosChanged EVENT 처리
+-------------------------------------------------------------------------------------------------*/

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch ( obj.id ) {
		//회선 목록 
		case "dsLineMana" :

			 if(nRow>-1){	
					if(dsLineMana.GetRowType(dsLineMana.row) ==  "insert"){

							tbLine.tbEquipHist.divEquipHist.fcInit();

							//신규 행일 경우   초기화 
							sEquipDistNo = "";

					}else if(dsLineMana.GetRowType(dsLineMana.row) ==  "normal" || dsLineMana.GetRowType(dsLineMana.row) ==  "update"){

							fcd_AddParam("EQUIP_DIST_NO", dsLineMana.GetColumn(nRow,"SEQ"));
							fcd_AddParam("CHECK_EQUIP_GBN", "003");

							fcSearch(obj,dsLineMana.GetColumn(nRow,"SEQ"),"003");

							sEquipDistNo = dsLineMana.GetColumn(nRow,"SEQ");

					}

					tbLine.tbLineMana.edLineNo.SetFocus();
			  }


			 break;

	}
}

/*-------------------------------------------------------------------------------------------------+
>>  dataset 에서 OnloadCompleted EVENT 처리
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsLineComp" :
			dsLineComp.InsertRow(0);
			dsLineComp.SetColumn(0, "CD_NM", "전체");
			cboSearchLineComp.index = 0;

			break;


	}

}

/*-------------------------------------------------------------------------------------------------+
>>  Tab 에서 OnChanging 처리
+-------------------------------------------------------------------------------------------------*/
function fcTabOnChanging(obj,nOldIndex,nNewIndex)
{
	switch ( nNewindex ) {

		case 0:	// 기본정보

			break;

		case 1 : //장비이력관리

			if(dsLineMana.GetRowCount() <=0 || dsLineMana.GetRowType(dsLineMana.row) == "insert"){
				fnc_Message(SNIS_RBM_A001, "","저장된 회선정보","장비이력");

				tbLine.TabIndex = 0;
			}

			break;

	}


}

