/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 회선  조회 완료 후
	if ( sServiceName == "rsy4030-service:searchLineMana" ) {

		tbLine.TabIndex = 0;
		tbLine.tbEquipHist.divEquipHist.fcInit();

	}


	// 회선   저장 완료 후
	if ( sServiceName == "rsy4030-service:saveLineMana" ) {

		fcSearch();
	}


}


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.id ) {

		//조회조건 구축년도 
		case "cboSearchLineComp" :
			fcSearch();
			break;

	}


}


function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	 switch ( obj.id ) {
		case "edSearchProdNm" :
			if ( nChar == ENTER_KEY ) {		
				fcSearch();		

			}
			break;	



	}
}
