/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// IP 조회 완료 후
	if ( sServiceName == "rsy4040-service:searchIpMana" ) {
		trace(dsIpMana.RowCount()+"건 조회 완료");
	}


	// IP 저장 완료 후
	if ( sServiceName == "rsy4040-service:saveIpMana" ) {
	    alert("저장되었습니다.");
		fcSearch();

	}

	if (sServiceName == "common-service:searchCode") {
		if (sMenuId == "RBR2030") {
			dsLocCd.filter("CD_TERM2='"+gdsUser.GetColumn(0, "TEAM_CD")+"'");
			//dsLocCd.filter("CD_TERM2='GA126100'");
			fcSearch();
		}
    }
}




/*-------------------------------------------------------------------------------------------------+
>> Enter Key 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{

	switch ( obj.id ) {
		case "edUseManNm" :
			if ( nChar == 13 ) {		
				fcSearch();		

			}
			break;

	}


}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsEquipCd" :
			dsEquipCd.InsertRow(0);
			dsEquipCd.SetColumn(0, "CD_NM", "전체");
			cboEquipCd.index = 0;
			break;
		case "dsLocCd" :
			if (sMenuId <> "RBR2030") {
				dsLocCd.InsertRow(0);
				dsLocCd.SetColumn(0, "CD_NM", "전체");
				cboLocCd.index = 0;
			}
			break;

	}


}



function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	 switch ( obj.id ) {
		case "edUseManNm" :
		case "edIp" :
		case "edManaDeptNm" :
			if ( nChar == ENTER_KEY ) {		
				fcSearch();		

			}
			break;	



	}
}

