}

function fcOnSize(obj,nCx,nCy,nState)
{		
	fcSetResize(obj,nCy);
}



// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nY)
{	// 해당화면은 기준사이즈보다 Height가 크기때문에 스크롤바 생성발생.그에 따라 그리드사이즈 조정
	var arrObj = array(-1);
	arrObj[0] = "grdIpMana";
	var nHeight = nY-28;

	if( nY <=580){
		grdIpMana.AutoFit = false;
		grdIpMana.height = 479;		
		return;
	}else{			
		grdIpMana.height = nHeight;
		if( nY < 1000) grdIpMana.AutoFit = false;
		else grdIpMana.AutoFit = true;			
	}	

}



function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id) {
		case "dsIpMana":
			if (strColumnID == "IP_REPR") {
				var ipRepr = obj.getColumn(nRow, "IP_REPR");
				var ipConv = split(ipRepr, ".");
				var ip = "";
				for (var icnt=0;icnt<ipConv.length;icnt++) {
					if (icnt > 0) {
						ip += ".";
						ip += lpad(ipConv[icnt],'0',3);					
					} else {
						ip += ipConv[icnt];					
					}
				}	
				obj.setColumn(nRow, "IP", ip);
			}
			break;			
	}
}
