var menuState = "";// 메뉴 상태 체크 
function TopMenu_OnClick(obj,nX,nY)
{   
	//Trace("TopMenu_OnClick:"+);
    NowSelectUpMenu = dsMenuGrpValue.getColumn(dsMenuGrpValue.FindRow("DIVID",obj.GetForm().id),"MN_ID");
	TopMenuSelect(obj.id);
}

function TopMenuSelect(menuid){

	 if(menuid == "" || menuid ==  null){
		//Trace("TopMenuSelect return: "+ menuid);
		return;
	 }

	if ( FocusMenu != null )
	{ 
		fcTopMenuProc(FocusMenu,"off");
		//Trace("FocusMenu: "+ FocusMenu);
		//열린 메뉴클릭시 그냥 닫음 
		if(FocusMenu = right(menuid,2) && menuState){
			menuState = false;
			return;
		}
	} else { 
		SubMenuDivHeightSet();
	}	

	var lstr = "DivTop" + right(menuid,2) + ".";

	object(lstr + "imgPT" + right(menuid,2)).ImageID = "open_f";
	object(lstr + "imgPT" + right(menuid,2)).Font = "굴림,9,bold";


	FocusMenu = right(menuid,2);
	fcTopMenuProc(FocusMenu,"on");
    menuState = true;
    global.FrameBottom.sNewUpMenu = "";
}

