function fcTopMenuProc(seq,flag)
{ 

	var DivTopstr;

	if ( flag == "off" )		
	{
		for ( var i = toInteger(seq) ; i <= nTotTopMenu ; i++ )
		{
			DivTopstr = "DivTop" + lpad(i,'0',2);
			if ( i == toInteger(seq) )
			{
				Object(DivTopstr + ".imgPT" + lpad(i,'0',2)).ImageID = "close_f";
				Object(DivTopstr + ".imgPT" + lpad(i,'0',2)).Font = "돋움,9,Bold";
				fcClear_SubMenu(object(DivTopstr),lpad(i,'0',2));
			}

			object(DivTopstr).Top = startTop + ((i-1)*23) ;
			if ( i == nTotTopMenu )
				object(DivTopstr).Height = 22;
			else
				object(DivTopstr).Height = 23;

		}
	}
	else
	{
		var nPos = (startTop + ((toInteger(seq) - 1) * 23));
		var nSubCnt;
		for ( var i = toInteger(seq) ; i <= nTotTopMenu ; i++ )
		{
			DivTopstr = "DivTop" + lpad(i,'0',2);
			if ( i == toInteger(seq) )
			{
				Object(DivTopstr + ".imgPT" + lpad(i,'0',2)).ImageID = "open_f";
				Object(DivTopstr + ".imgPT" + lpad(i,'0',2)).Font = "돋움,9,Bold";
				nSubCnt = toNumber(object(DivTopstr).Static0.Text);
				//28->22 로 변경 !! 
				object(DivTopstr).Height =  ( decode(nSubCnt,0,1,nSubCnt) * 23 ) + decode(nSubCnt,0,0,22);

				//alert(object(DivTopstr).Height);
				object(DivTopstr).Top = nPos;
				nPos += object(DivTopstr).Height;
			}
			else
			{
				object(DivTopstr).Top = nPos;
				nPos += 23;
			}	
		}
	}

}
function fcClear_SubMenu(DivTopObj,seq)
{
	//alert("clear SubMenu");

	var nCnt = toNumber(DivTopObj.Static0.Text);
	var strsub = DivTopObj.id + ".DivSub" + seq + ".";
	for ( var i = 1 ; i <= nCnt ; i++ )
	{
		object(strsub + "imgCT" + lpad(i,'0',2)).font = "돋움,9";
		object(strsub + "imgCT" + lpad(i,'0',2)).color = "#656565";
		object(strsub + "imgCT" + lpad(i,'0',2)).imageID = "off_icon";
	}
}

function SubMenuDivHeightSet()
{ 
	var DivTopObj;
	var nCnt;
	for ( var i = 1 ; i <= nTotTopMenu ; i++ )
	{
		DivTopObj = object("DivTop" + lpad(i,'0',2));
		nCnt = toNumber(DivTopObj.Static0.Text) * 23;
		object("DivTop" + lpad(i,'0',2) + '.DivSub' + lpad(i,"0",2)).height = nCnt;
	}
	var CurDiv = Object("DivTop" + lpad(1,"0",2)); 
}

function fcGen_topMenu()
{

	for(var i =0; i < 15 ; i++){	
		//if(Length(this.find("DivTop0"+i).ID) > 0){
		if(Length(this.find("DivTop"+lpad(ToString(i),"0",2)).ID) > 0){						
			//Destroy("DivTop"+lpad(i,"0",2);
			Destroy("DivTop"+lpad(tostring(i),"0",2));

		}
	}

	var seq = 1;
	var TopPos = startTop;
	var CurDiv;
	var CurDivContent = "";
	var SubTopPos = 0;
	var SubCount = 0;
	var Subseq = 1;


	for ( var i = 0 ; i < dsMenuGrpValue.rowcount ; i++ )
	{		
		if ( dsMenuGrpValue.getcolumn(i,"LVL") == 1 )
		{
			if ( CurDivContent.length() > 0 )
			{
				CurDivContent += "	</Contents>";
				CurDivContent += "</Div>";
				CurDivContent += '<Static Visible="false" Height="13" Id="Static0" Left="311" TabOrder="9" Text="' + SubCount + '" Top="65" Width="40"></Static>';
				CurDivContent += "</Contents>";

				CurDiv.Contents = CurDivContent;
				seq++;
			}

			SubTopPos = 0;
			SubCount = 0;
			Subseq = 1;
			Create("Div","DivTop" + lpad(seq,"0",2),
			'Height="23" left="5" Top="' + TopPos + '" Width="191"');
			TopPos += 23;

			CurDiv = Object("DivTop" + lpad(seq,"0",2));
			CurDivContent = "<Contents>";
			CurDivContent += '<Image Height="14" Id="imgD' + lpad(seq,"0",2) + '"   TabOrder="1" Top="3" Transparent="TRUE" Width="1"></Image>';
			CurDivContent += '<Image Align="Left"  Cursor="HAND" Color="#656565"  Font="돋움,9,Bold" ImageID="close_f" Height="23" Id="imgPT' + lpad(seq,"0",2) + '" Left="0" OnClick="TopMenu_OnClick" Static="FALSE" TabOrder="2" Text="  ' + dsMenuGrpValue.getcolumn(i,"MN_NM") + '" Width="191"></Image>';
			//CurDivContent += '<Shape Bottom="22" Height="2" Id="ShapeT1" Left="10" LineColor="white" Right="192" TabOrder="3" Top="20" Width="190"></Shape>';
			//CurDivContent += '<Shape Bottom="21" Height="2" Id="ShapeT2" Left="10" LineColor="#d1d1d1" Right="192" TabOrder="4" Top="19" Width="190"></Shape>';
			CurDivContent += '	<Div BKColor="white"  Height="48" Id="DivSub' + lpad(seq,"0",2) + '" Left="0" TabOrder="5"  Text="Div0" Top="23" Width="191">';
			CurDivContent += '		<Contents>';

			dsMenuGrpValue.SetColumn(i,"DIVID","DivTop" + lpad(seq,"0",2));
			dsMenuGrpValue.SetColumn(i,"IMGPT","imgPT" + lpad(seq,"0",2));

			//Trace(i+"----imgPT=>" + dsMenuGrpValue.getColumn(i,"IMGPT"));
		}
		else
		{


			SubCount++;
//			CurDivContent += '<Image Align="Left" Color="#656565" Cursor="HAND" DropMode="TRUE" ImageID="off_icon" Font="돋움,9" Height="23"  Left="0" Id="ImgCT' + lpad(Subseq,"0",2) + '" OnClick="SubTopMenu_OnClick"  Static="FALSE" TabOrder="2" Top="' +  SubTopPos + '" Text="        ' + dsMenuGrpValue.getcolumn(i,"MN_NM") + '"  OnClick="fcSearchMenu" Width="191" userData="' + i + '"></Image>';
			CurDivContent += '<Image Align="Left" Color="#656565" Cursor="HAND" DropMode="TRUE" ImageID="off_icon" Font="돋움,9" Height="23"  Left="0" Id="ImgCT' + lpad(Subseq,"0",2) + '" Static="FALSE" TabOrder="2" Top="' +  SubTopPos + '" Text="        ' + dsMenuGrpValue.getcolumn(i,"MN_NM") + '"  OnClick="fcSearchMenu" Width="191" userData="' + i + '"></Image>';
			//SubTopPos += 6;
			CurDivContent += '<Image Height="7"    Id="Img' + lpad(Subseq,"0",2) + '"  Left="0" TabOrder="1" Top="' + SubTopPos + '" Width="1"></Image>';
			SubTopPos += 23;

			dsMenuGrpValue.SetColumn(i,"DIVID","DivSub" + lpad(seq,"0",2));
			dsMenuGrpValue.SetColumn(i,"IMGPT","ImgCT" + lpad(Subseq,"0",2));
			//CurDivContent += '<Image FillType="TILE" Height="1" Id="ImgLine' + lpad(Subseq,"0",2) + '" ImageID="dot_iine" Left="1" TabOrder="3" Top="' +  SubTopPos +'" Width="170"></Image>';
			//SubTopPos += 1; 
			Subseq++;

		}

		CurDiv.border="FLAT";
	}

	if ( CurDivContent.length() > 0 )
	{

		CurDiv.Height -= 2;
		CurDivContent += "	</Contents>";
		CurDivContent += "</Div>";
		CurDivContent += '<Static Visible="false" Height="13" Id="Static0" Left="311" TabOrder="9" Text="' + SubCount + '" Top="65" Width="40"></Static>';
		CurDivContent += "</Contents>";
		CurDiv.Contents = CurDivContent;
		nTotTopMenu = seq;
		//CurDiv.border="FLAT"; 
	}



}

