function fcSearch(obj) {
	var sServiceName = "common-service:SearchMenuGrp";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuGrpValue=dsMenuGrpValue ";
        sOutDataSet += "dsMenuGrpList=dsMenuGrpList ";    

	var sMnGrpCd = obj;
	var sTopMnMm = gdsTopMenu.GetColumn( gdsTopMenu.FindRow("MN_ID",sMnGrpCd)  , "MN_NM");
	if(sMnGrpCd =="R000002" && sTopMnMm == ""){
		sTopMnMm ="지점현황";
	}

	var sUserIp = ext_GetIPAddress();
	if ( Pos(sUserIp,",") != -1) {
		sUserIp = mid(sUserIp, 1, Pos(sUserIp,",") -1);
	} else {
		sUserIp = Replace(Replace(sUserIp, "["), "]");
	}

	tab_menu.page0.Image0.Text = sTopMnMm;

	fcd_AddParam("MN_GRP", sMnGrpCd);
	fcd_AddParam("USER_ID", gdsUser.getColumn(0, "USER_ID"));
	fcd_AddParam("USER_IP", Trim(sUserIp));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


function fcSearchMenuList(obj,nOldRow,nRow) {
