function fcSelectMenu(divid, imgid, menuid){
	var seq = right(divid,2);
	fcClear_SubMenu(object("DivTop" + seq),seq);
	var strsub = "DivTop" + seq + ".DivSub" + seq + ".";
	var subseq = right(imgid,2);
	object(strsub + "imgCT" + subseq).font = "굴림,9,bold";
	object(strsub + "imgCT" + subseq).imageID = "on_icon";
	object(strsub + "imgCT" + subseq).color = "#067ca2";
    global.FrameBottom.fcOpenMdiForm(menuid);
    global.FrameBottom.sNowMenu = ""; 
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 메뉴리스트 조회 완료 후rsy0020-service:searchMenuGrpList
	if ( sServiceName == "common-service:SearchMenuGrp" ) {
		//alert("SearchMenuGrp");

		//trace(dsMenuGrpValue.SaveXML());
        //trace(dsMenuGrpList.SaveXML());



		tab_menu.TabIndex = 0;

		 // 메인 TOP 메뉴 클릭시 초기화 필요 
		 FocusMenu = null;
		 startTop = 55; //35;
		 nTotTopMenu = 0;		 
		 fcGen_topMenu();

		 Idle();
		 fcChangeMenuView(global.FrameBottom.sNewUpMenu, global.FrameBottom.sNowMenu);

		 if (gdsUser.GetColumn(0, "GBN") != "005")  return;

		 if(global.FrameTop.sTopFirstMenu == "RBB1010"){			
			global.FrameBottom.fcOpenMdiForm("RBB1080","최근게시판","frm_rbb::RBB1080.xml");
			global.FrameTop.sTopFirstMenu ="";
		 }
	}	
}

function fcChangeMenuView(sNewUpMenu, sNowMenu){
	//trace(sNewUpMenu+" : "+sNowMenu+" : "+NowSelectUpMenu);

	if( sNewUpMenu != "" &&  sNewUpMenu != null){
		 var upimgid = dsMenuGrpValue.getColumn(dsMenuGrpValue.FindRow("MN_ID",sNewUpMenu),"IMGPT"); 

		if(NowSelectUpMenu != sNewUpMenu){
			NowSelectUpMenu = sNewUpMenu;
			TopMenuSelect(upimgid);	
		}

		if(sNowMenu != "" && sNowMenu != null){ 
			//Idle();
		     //var rowpos = dsMenuGrpValue.FindRow("MN_ID",sNowMenu);
		     for( var i = 0 ; i < dsMenuGrpValue.count ; i++){
				if(sNowMenu == dsMenuGrpValue.getColumn(i,"MN_ID")){
					 var imgid  = dsMenuGrpValue.getColumn(i,"IMGPT"); 
					 var menuid = dsMenuGrpValue.getColumn(i,"MN_ID"); 
					 var divid  = dsMenuGrpValue.getColumn(i,"DIVID"); 
					 //Trace(i+" " +divid+" "+ imgid+" "+ menuid);
					 fcSelectMenu(divid, imgid, menuid);
					 break;
				 }
			}
		}
	}
}
function fctabOnChanged(obj,nOldIndex,nNewindex)
{
	if (nNewIndex == 1) {	//즐겨찾기
		fcsetDivVisible(false);
	} else {
		fcsetDivVisible(true);
	}	
}

function fcsetDivVisible(bVisible)
{
	for( var i= 0;  i < this.Components.Count(); i++)
	{
		var objType = toLower(this.Components[i].getType());
		if(objType <> "div") continue;
		var obj = Object(this.Components[i].id);
		obj.visible = bVisible;
	}	
}

