function fcFormLoadSetting(obj) { 
    //화면환경 설정
    sFORMCAPTION  = obj.Title;
    sFORMLOCATION = "";


	// 최초 접속시 PC의 시간을 서버시간으로 동기화 한다.
	LocalTimeSetting();


    // 공지사항 팝업 화면 호출
    //Dialog("common::comInfoPopup.xml", "", "", "", "");
    //Dialog("common::comInfoPopup.xml","","","","","-1","80");


    if(Length(gdsUser.GetColumn(0,"AUTH_TEAM_CD")) >0){

		if(gdsUser.GetColumn(0,"TEAM_CD") != gdsUser.GetColumn(0,"AUTH_TEAM_CD") ){

			fnc_Message(SNIS_RBM_0006,"N");
		}
     }
    SetLastLoginInfo();	// 이전 로그아웃 정보 저장

    dsOutNotice.Filter("sType='1'");

    global.FrameBottom.fnLinkOpen(); 

    tbBord.TabIndex = 1;
    tbWork.TabIndex = 0;

    fcSearch();

    fcCheckPriv();

    // 최근 접속정보를 가져온다
    fcSearchLastLoginInfo(obj);

    /*------------
	if(gdsUser.GetColumn(0,"USER_ID")=="05071") {
		var getIP =  ext_GetIPAddress();
		if(getIP[0]!="160.100.52.16") {
			alert("관리자 IP가 맞지않습니다.");
			exit();
		}
	} 

    DeleteShortCut();
    --------*/
}


