        iCnt = gdsDbStatus.FindRow("CD","JUPITER");
        if (iCnt >= 0) {
        	if (gdsDbStatus.GetColumn(iCnt, "CD_NM") == "1") return;
		}
		if (gdsUser.GetColumn(0, "GBN") == "005") {
			fcSearchNoticeBbs(); //KSPO 공지사항
			fcSearchMraBbs();	 //KSPO 경륜.경정 게시판 
			fcSearchBrncBbs();   //KSPO 지점 게시판 
        }
        */
        fcSearchMyMenu();
}


/*-------------------------------------------------------------------------------------------------+
>>  처리대상업무 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchProcWork(){

    var sServiceName = "common_main-service:searchProcWork";
    var sInDataSet   = "";
    var sOutDataSet  = "dsProcWork=dsProcWork ";


    fcd_AddParam("TEAM_CD", sDeptCd);
    fcd_AddParam("BRNC_CD", sBrncCd);
    fcd_AddParam("USER_ID", sUserId);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

