/*-------------------------------------------------------------------------------------------------+
>>  공지사항 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchNotice(){

    var sServiceName = "common_main-service:searchNotice";
    var sInDataSet   = "";
    var sOutDataSet  = "dsNotice=dsNotice ";


    fcd_AddParam("CLS_CD", "001");

    var searchKey = "";
    switch ( tbWork.tbNotice.cboNoticeSearchGbn.index ) {
        case 0 : searchKey = "TITLE";  break;
        case 1 : searchKey = "BORD_DESC";   break;
    }

    fcd_AddParam(searchKey, tbWork.tbNotice.edSearchNoticeText.text);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

