function fcInit() {
	dsAlarmHist.ClearData();
	dsNotice.ClearData();
	dsQa.ClearData();

	dsBrncCompareSales.ClearData();
	dsBranchAmt.ClearData();
	dsRaceDayInfo.ClearData();
	dsSaleChart.ClearData();

	dsProcWork.ClearData();
	dsAlarmHist.ClearData();


}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if(sServiceName == "common_main-service:searchBrncCompareSales")
    {

       // dsBrncCompareSales.ClearData();

        var sBranchSale = dsRaceDayInfo.GetColumn(0,"RACE_DAY")+" ";
            sBranchSale += "("+dsRaceDayInfo.GetColumn(0,"RACE_YOIL_KOR")+") ";
            sBranchSale += dsRaceDayInfo.GetColumn(0,"MEET_NM") +" "+dsRaceDayInfo.GetColumn(0,"TMS")+"회차 ";
            sBranchSale += dsRaceDayInfo.GetColumn(0,"DAY_ORD")+"일차";

        stBranchSale.Text = "지점별 매출액 "+sBranchSale;
        // 그린카드 합산금액으로 변경(2014.4.11)

        var nCount=0;
        for(var i=0;i<dsBranchAmt.rowcount/2;i++)
        {
            dsBrncCompareSales.AddRow();
            dsBrncCompareSales.SetColumn(nCount,"TDIV_NO",dsBranchAmt.GetColumn(i*2,"TDIV_NO") );
            dsBrncCompareSales.SetColumn(nCount,"BRNCH_NM",dsBranchAmt.GetColumn(i*2,"BRNCH_NM") );
            dsBrncCompareSales.SetColumn(nCount,"N_DIV_TOTAL",dsBranchAmt.GetColumn(i*2,"N_DIV_TOTAL") );
            dsBrncCompareSales.SetColumn(nCount,"B_DIV_TOTAL_SIGN",dsBranchAmt.GetColumn(i*2,"B_DIV_TOTAL_SIGN") );

            dsBrncCompareSales.SetColumn(nCount,"TDIV_NO_2",dsBranchAmt.GetColumn(i*2+1,"TDIV_NO") );
            dsBrncCompareSales.SetColumn(nCount,"BRNCH_NM_2",dsBranchAmt.GetColumn(i*2+1,"BRNCH_NM") );
            dsBrncCompareSales.SetColumn(nCount,"N_DIV_TOTAL_2",dsBranchAmt.GetColumn(i*2+1,"N_DIV_TOTAL") );
            dsBrncCompareSales.SetColumn(nCount,"B_DIV_TOTAL_SIGN_2",dsBranchAmt.GetColumn(i*2+1,"B_DIV_TOTAL_SIGN") );
            nCount++;
        }
    } else if ( sServiceName == "common_main-service:searchAmountProcess" ) {
        fcSetAmountChart();        
    } else if ( sServiceName == "common-service:searchCode" ) {
		if (dsDB.rowcount() > 0) {
			FrameTop.st_db_check.text = dsDB.GetColumn(0, "CD_NM");
		}
		if (dsMeetCd.RowCount() > 0) {
			var nDay = GetDay(Today());
			 if (nDay >= 2 && nDay <= 4) rdoMeetCd.Index = 1;
			 else                        rdoMeetCd.Index = 0;
		}
	} else if (sServiceName == "rbb1010-service:searchNoticeBbs") {
		if (dsProcWork.rowcount() > 0) {
			global.FrameBottom.fcOpenMdiForm("R000000", "메인화면", "common::comMain.xml");
		}
	} else if ( sServiceName == "common_login-service:searchLastLoginInfo") {
		if (dsLastLoginInfo.rowcount() > 0) {
			fcDisplayLastLoginInfo();
		} 
	} else if ( sServiceName == "common_login-service:SaveUserTrace") {
		SetReg("LastLogSeq", "");
		SetReg("LastLoginDt", "");
	}

}
