function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	switch ( obj.id ) {
		case "grdNotice" : //공지사항 그리드 
			var sSeqNo = dsNotice.getColumn(nRow, "SEQ_NO");
			var sClsCd = dsNotice.getColumn(nRow, "CLS_CD");


			// Content로 넘겨지는 arguements
			var sArg    = "Arg_seqNo="  + quote(sSeqNo);
				sArg   += " Arg_clsCd=" + quote(sClsCd);
				sArg   += " sEditYn=N";

			if ( Dialog("frm_rsy::RSY5020.xml", sArg, 250, 250, "","200","150") ) {
				fcSearchNotice();
			}	

			break;

		case "grdQa" : //공지사항 그리드 
			var sSeqNo = dsQa.getColumn(nRow, "SEQ_NO");
			var sClsCd = dsQa.getColumn(nRow, "CLS_CD");


			// Content로 넘겨지는 arguements
			var sArg    = "Arg_seqNo="  + quote(sSeqNo);
				sArg   += " Arg_clsCd=" + quote(sClsCd);
				sArg   += " sEditYn=N";

			if ( Dialog("frm_rsy::RSY5020.xml", sArg, 250, 250, "","200","150") ) {
				fcSearchQa();
			}

			break;

		case "grdProcWork" : //처리업무 그리드 
			var sMnId = dsProcWork.getColumn(nRow, "MN_ID");

			var sTopMenu = gdsMenu.GetColumn(gdsMenu.FindRow("MN_ID",sMnId), "TOP_MENU_ID");
				 global.FrameRight.fcSearch(sTopMenu);
				 global.FrameTop.sTopFocusMenu= sTopMenu;

				 global.FrameBottom.fcOpenMdiForm(sMnId);

			break;

	   case "grdAlarmHist" : //처리업무 그리드 
			var sMnId = dsAlarmHist.getColumn(nRow, "MN_ID");

			var sTopMenu = gdsMenu.GetColumn(gdsMenu.FindRow("MN_ID",sMnId), "TOP_MENU_ID");
				 global.FrameRight.fcSearch(sTopMenu);
				 global.FrameTop.sTopFocusMenu= sTopMenu;

				 //var strParam = "sSearchRecvUserId="+sUserId;
				 //GBL_RET_VALUE=strParam; // 값 생성  
				 global.FrameBottom.fcOpenMdiForm(sMnId);

			break;


	}


}

function fcBtnOnClick(obj)
{
	switch ( obj.id ) {
		case "btnNoticeSearch" : // 공지사항  검색 			 
			fcSearchNotice();
			break;

		case "btnQaSearch" :    //QA 검색 			 
			fcSearchQa();
			break;

		case "btnQaWrite" : //QA 글쓰기	
			var sArg  = "Arg_clsCd=" + quote(sQAClsCd);			
			if ( Dialog("frm_rsy::RSY5020.xml", sArg, 250, 250, "","200","150") ) {
				fcSearchQa();
			}			
			break; 

		case "btn_refresh" : // 공지사항  검색 
			tbBord.TabIndex = 0;
			tbWork.TabIndex = 0;

			tbWork.tbNotice.edSearchNoticeText.Text ="";
			tbWork.tbQa.edSearchQaText.Text = "";

			fcSearch();
			break;

		case "btnSendSms":		// SMS전송을 위한 팝업 띄우기

			var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
			var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
			var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
			var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
			var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
			var sParams				= "";
			var sReceiveId          = "";
			var sTitle              = "수동SMS전송";
			var sPermTempGbn        = "002";						// '001" 정규직, "002": 수신자목록을 Dataset으로 넘겨줌

			sParams  = "sSendPhoneNumber="+		quote(sSendPhoneNumber);
			sParams += " sSendUserId="+			quote(sSendUserId);
			sParams += " sSendUserName="+		quote(sSendUserName);
			sParams += " sReceivePhoneNumber="+	quote(sReceivePhoneNumber);
			sParams += " sReceiveName="+		quote(sReceiveName);
			sParams += " sReceiveId="+			quote(sReceiveId);
			sParams += " sTitle="+				quote(sTitle);
			sParams += " sRecvrUserType="+		quote(sPermTempGbn);

			nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");

			break;	                    

	}
}

