// 그리드 클릭 했을 때 이벤트
function fcOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{	
	//alert(obj.ID + ":fcOnCellClick");
	switch ( obj.ID ) {			
		case "grdBrncCompareSales" :	// 지점별 매출액 클릭 했을 때
			// 매출액 비교 페이지로 보낼 파라미터 생성
			var COMM_NO	="";
			var BRNCH_NM = "";
			if(nCell==0)
			{
				COMM_NO	= decode(dsBrncCompareSales.GetColumn(nRow,"TDIV_NO"),null,'',dsBrncCompareSales.GetColumn(nRow,"TDIV_NO"));
				BRNCH_NM = decode(dsBrncCompareSales.GetColumn(nRow,"BRNCH_NM"),null,'',dsBrncCompareSales.GetColumn(nRow,"BRNCH_NM"));
			}
			if(nCell==3)
			{
				COMM_NO	= decode(dsBrncCompareSales.GetColumn(nRow,"TDIV_NO_2"),null,'',dsBrncCompareSales.GetColumn(nRow,"TDIV_NO_2"));
				BRNCH_NM = decode(dsBrncCompareSales.GetColumn(nRow,"BRNCH_NM_2"),null,'',dsBrncCompareSales.GetColumn(nRow,"BRNCH_NM_2"));
			}				

			var RACE_DAY = replace(dsRaceDayInfo.GetColumn(0,"RACE_DAY"),"-","");

			var strParam = "RACE_DAY="+RACE_DAY;
			strParam +=" MEET_CD="+dsRaceDayInfo.GetColumn(0,"MEET_CD");
			strParam +=" COMM_NO="+COMM_NO;
			strParam +=" BRNCH_NM="+BRNCH_NM;
			GBL_RET_VALUE=strParam; // 값 생성	

			if(BRNCH_NM!="" and BRNCH_NM !="전체" and BRNCH_NM !="교차") // 교차는 자료가 원래 안나왔고, 전체는 문제의 소지가 있어서 표현하지 않음 20150425
			{
				global.FrameBottom.fcOpenMdiForm("RSM2011", "매출액비교", "frm_rsm::RSM2011.xml");
			}
		break;

	}

}

