function fcDisplayLastLoginInfo()
{
	GBL_LASTLOGININFO  = "  최근 접속일시 : " + dsLastLoginInfo.GetColumn(0, "LOGIN_DT");	
	GBL_LASTLOGININFO += ", " + dsLastLoginInfo.GetColumn(0, "PC_IP");	
	GBL_LASTLOGININFO += ", " + iif(length(JSESSIONID) < 40, "L", substr(JSESSIONID, Pos(JSESSIONID,".") + 7, 1)); 
	//if (GBL_DUP_LOGIN_CHK == "Y") fnc_message(GBL_LASTLOGININFO, "N");

	if ( Global.FrameBottom != null || stLastLoginInfo != null ) {
		stLastLoginInfo.text = GBL_LASTLOGININFO;
	}	
}

