/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

     sBUTTONLIST   = 'FFFFFFT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	stFormTitle2.Text = sTitle;

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	stFormTitle.Text  = "게시판조회 > 경륜경정사업본부 > 최근게시판(RBB1080)";

	fcSearch();

	fcSetResize(this,this.width,this.height);
}


/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
	dsBbs.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	// 전자결재 DB 장애 여부 체크 : 장애시 조회 안함
	var iCnt = 0;
	iCnt = gdsDbStatus.FindRow("CD","JUPITER");
	if (iCnt >= 0) {
		if (gdsDbStatus.GetColumn(iCnt, "CD_NM") == "1") return;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbb1010-service:searchBbs";
	var sInDataSet   = "";
	var sOutDataSet  = "dsBbs=dsBbs ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbb1010-service:searchBbs" ) {

	}

}



function fcGrdOnCellDBLClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

		var params="";
		params+="sDocYearMON="+dsBbs.GetColumn(nRow,"DOC_YEARMON");
		params+=" sDocNumber="+dsBbs.GetColumn(nRow,"DOC_NUMBER");
		params+=" sBbsId="+dsBbs.GetColumn(nRow,"BBS_ID");

		Dialog("frm_rbb::RBB1011.xml", params, "862", "654",  "Scroll=true,titlebar=true,Resize=true,Autosize=false", 10, 10);
}

function fcGrdOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
		if(Length(dsBbs.GetColumn(nRow,"BBS_ID")) <=0){
			return;
		}

		var params="";
		params+="sDocYearMON="+dsBbs.GetColumn(nRow,"DOC_YEARMON");
		params+=" sDocNumber="+dsBbs.GetColumn(nRow,"DOC_NUMBER");
		params+=" sBbsId="+dsBbs.GetColumn(nRow,"BBS_ID");

		Dialog("frm_rbb::RBB1011.xml", params, "862", "654",  "Scroll=true,titlebar=true,Resize=true,Autosize=false", 170, 40);
}

function fcEnd(obj) {

	// 화면 종료
	Close();
}

function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsBbs";
	if (   !fnc_FormUnloadCheck(sDataset  , "RBB1080") ) return false;
}

function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{		
	if( nY <= 576) {
		grdBusiNotice.Bottom = 568;
		grdBusiNotice.height = 476;
	} else {
		grdBusiNotice.Bottom = nY - grdBusiNotice.top + 72;
	}	
}
