	fcInit();

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {
	if ( !fcSearchCheck() ) return;

	var sServiceName = "rbo3010-service:searchRollMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsRollMana=dsRollMana ";
		//sOutDataSet += "dsIpMana=dsIpMana ";

	fcd_AddParam("STND_YEAR", cboStndYear.Value);
	fcd_AddParam("TMS", cboTms.Value);
	fcd_AddParam("BRNC_CD", cboSearchBrncCd.Value);
	fcd_AddParam("ROLL_GBN", cboRollGbn.Value);


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


