	var sServiceName = "rbo3010-service:searchStndYear";
	var sInDataSet   = "";
	var sOutDataSet  = "dsStrYear=dsStrYear ";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsRollMana) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsRollMana;
	sMandatoryColID   = "ROLL_GBN";
	sMandatoryColName = "용지구분";
	oGrid             = grdRollMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {    
	if ( !fcSaveCheck() ) return;


	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


    var sUsrIp = ext_GetIPAddress();
    for(var i = 0; i < dsRollMana.GetRowCount() ; i++){
		if( dsRollMana.getRowType(i) == "update" || dsRollMana.getRowType(i) == "insert"){
			dsRollMana.SetColumn(i,"IP",sUsrIp[0]);

		}
    }


	sServiceName = "rbo3010-service:saveRollMana";
	sInDataSet   = "dsRollMana=dsRollMana:u ";

	sOutDataSet  = "dsRollMana=dsRollMana ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

