/*-------------------------------------------------------------------------------------------------+
>> dataset  load 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{

	switch ( obj.id ) {
		case "dsRollGbn" :
			dsRollGbn.InsertRow(0);
			dsRollGbn.SetColumn(0, "CD_NM", "전체");
			cboRollGbn.index = 0;
			break;
		case "dsBrncCd" :
			dsBrncCd.InsertRow(0);
			dsBrncCd.SetColumn(0, "CD_NM", "전체");
			cboSearchBrncCd.index = 0;

			cboSearchBrncCd.Value = fcd_GetUserBrncCd();
			break;
		case "dsTms" :
			var sYear =fcd_GetSysdate("YYYY");

			if(cboStndYear.Value == sYear){
				cboTms.value = dsTms.GetColumn(0,"CUR_TMS");
			}else{
				cboTms.Index = 0;

			}


			var nRow = dsTms.FindRowNF("TMS_CD",cboTms.value);
			stRaceDay ="( " + fnc_DateFormat(dsTms.GetColumn(nRow,"RACE_STR_DAY"),"/") + " - " +fnc_DateFormat(dsTms.GetColumn(nRow,"RACE_END_DAY"),"/")  + " )";


			break;

	}


}



/*-------------------------------------------------------------------------------------------------+
>> combo  onchange 이벤트시 조회되도록 한다.
+-------------------------------------------------------------------------------------------------*/
function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    switch ( obj.id ) {
		case "cboStndYear" :
			fcd_GetTmsDs("dsTms", "001", cboStndYear.Value);

			fcSearch();
			break;
		case "cboTms" :
			var nRow = dsTms.FindRowNF("TMS_CD",strCode);
			stRaceDay ="( " + fnc_DateFormat(dsTms.GetColumn(nRow,"RACE_STR_DAY"),"/") + " - " +fnc_DateFormat(dsTms.GetColumn(nRow,"RACE_END_DAY"),"/")  + " )";

			fcSearch();
			break;
		case "cboSearchBrncCd" :
			fcSearch();
			break;
		case "cboRollGbn" :
			fcSearch();
			break;

	}
}

/*-------------------------------------------------------------------------------------------------+
>> 기준년도 콤보 dataset 설정 
+-------------------------------------------------------------------------------------------------*/
function setStndYear(sStrYear){

	var sCurYear  = ParseInt(fcd_GetSysdate("YYYY"));


	for(var i= sStrYear; i < sCurYear +1; i++){
		nRow = dsStndYear.AddRow();

		dsStndYear.SetColumn(nRow,"CD",i);
		dsStndYear.SetColumn(nRow,"CD_NM",i);		
	}

	cboStndYear.value = sCurYear;
}




function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{ 
	switch ( obj.id ) {
		case "dsRollMana" :

			var nTotCnt  = 0;

			var nPreStk  = 0;
			var nMoveCnt = 0;
			var nMakeCnt = 0;

			if(strColumnID == "MRA_USGE_CNT"){
				 nTotCnt  = dsRollMana.getColumn(nRow,"MRA_USGE_CNT" ) + dsRollMana.getColumn(nRow,"CRA_USGE_CNT" );

				 nPreStk  = dsRollMana.getColumn(nRow,"PRE_STK" );
				 nMoveCnt   = dsRollMana.getColumn(nRow,"IN_CNT" ) - dsRollMana.getColumn(nRow,"OUT_CNT" );
				 nMakeCnt = dsRollMana.getColumn(nRow,"MAKE_STK_CNT" );

				 dsRollMana.SetColumn(nRow,"TOT_USGE_CNT", nTotCnt);
				 dsRollMana.SetColumn(nRow,"STK_CNT", nPreStk + nTotCnt + nMoveCnt + nMakeCnt);
			}else if(strColumnID == "CRA_USGE_CNT"){

				 nTotCnt  = dsRollMana.getColumn(nRow,"MRA_USGE_CNT" ) + dsRollMana.getColumn(nRow,"CRA_USGE_CNT" );

				 nPreStk  = dsRollMana.getColumn(nRow,"PRE_STK" );
				 nMoveCnt   = dsRollMana.getColumn(nRow,"IN_CNT" ) - dsRollMana.getColumn(nRow,"OUT_CNT" );
				 nMakeCnt = dsRollMana.getColumn(nRow,"MAKE_STK_CNT" );

				 dsRollMana.SetColumn(nRow,"TOT_USGE_CNT", nTotCnt);
				 dsRollMana.SetColumn(nRow,"STK_CNT", nPreStk + nTotCnt + nMoveCnt + nMakeCnt);

			}else if(strColumnID == "MAKE_STK_CNT"){

				 nTotCnt  = dsRollMana.getColumn(nRow,"MRA_USGE_CNT" ) + dsRollMana.getColumn(nRow,"CRA_USGE_CNT" );

				 nPreStk  = dsRollMana.getColumn(nRow,"PRE_STK" );
				 nMoveCnt   = dsRollMana.getColumn(nRow,"IN_CNT" ) - dsRollMana.getColumn(nRow,"OUT_CNT" );
				 nMakeCnt = dsRollMana.getColumn(nRow,"MAKE_STK_CNT" );

				 dsRollMana.SetColumn(nRow,"TOT_USGE_CNT", nTotCnt);
				 dsRollMana.SetColumn(nRow,"STK_CNT", nPreStk + nTotCnt + nMoveCnt + nMakeCnt);

			}


			break;

	}

}

function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
		switch ( obj.id ) {
			case "dsRollMana" :
			if(!fcd_GetAuthByBrnc(dsRollMana.getColumn(nRow,"BRNC_CD"))){
				mskMraUsgeCnt.Enable = false;
				mskCraUsgeCnt.Enable = false;
				mskMakeStkCnt.Enable = false;
				edRmk.Enable = false;

			}else{



					if( dsRollMana.getColumn(nRow,"END_YN")  >  "0"){
							mskMraUsgeCnt.Enable = false;
							mskCraUsgeCnt.Enable = false;
							mskMakeStkCnt.Enable = false;
							edRmk.Enable = false;
					}else{
							mskMraUsgeCnt.Enable = true;
							mskCraUsgeCnt.Enable = true;
							mskMakeStkCnt.Enable = true;
							edRmk.Enable = true;
					}

					mskMraUsgeCnt.SetFocus();
		    }

				break;
	    }

}

