function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTTFTFT';

 	//È­¸éÈ¯°æ ¼³Á¤
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	calStartDate.Value = substr(getDate(), 0, 6) + "01";	// ÇöÀç´ÞÀÇ Ã¹³¯(¸Å¿ù 1ÀÏ)
	calEndDate.Value = getDate();

	//È­¸é Á¤º¸ Display ¹× Button ±ÇÇÑ ¼³Á¤
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//ÃÊ±â È­¸é BK Color¼³Á¤ 
	fnc_SetBKColor(this);

	//°øÅëÄÚµå Á¶È¸
	fcd_GetComboDs(dsInComCd);

	// ºÎÇ°¸ñ·ÏÁ¶È¸	
	http.Sync = true;
	getComboschPartsNm();
	http.Sync = false;

    // ¸ñ·Ï Á¶È¸ 
    fcSearch();  
}


