function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// ºÎÇ°¸ñ·Ï Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "rbo4040-service:searchSchParts") {

		/* µî·Ï¿¡ ÇØ´çÇÏ´Â ÇÊÅÍ¸µ */
		dsCd.Copy(dsSchP);			// ºÎÇ° (µî·Ï ÄÞº¸)


		// Combo ¿¡ ÀüÃ¼ Ãß°¡ : ¹ß¸Å±â, ºÎÇ°ºÐ·ù, ºÎÇ°
		dsSchP.fireEvent = false;
		dsSchP.InsertRow(0);		
		dsSchP.SetColumn(0,"PARTS_CD"   , "" );
		dsSchP.SetColumn(0,"PARTS_NM"	, "ÀüÃ¼");
		dsSchP.fireEvent = true;

		cboSchPartsCd.Index = 0;
	}

	//Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "rbo4020-service:searchList" ) {

	}			

	//ÀúÀå ¿Ï·á ÈÄ
	if ( sServiceName == "rbo4020-service:SavePartsIn" ) {

		fcSearch();		
	}	
}


