function fcAddRow(obj) {

	var nRow;	// Record Add

	nRow = dsList.AddRow();

	dsList.SetColumn(nRow, "BRNC_CD", "00"					);	// º»Àå : ±¤¸í
	dsList.SetColumn(nRow, "IN_DT"  , substr(getDate(),0,8)	);	// ÀÔ°íÀÏÀÚ

	grdList.SetFocus(nRow);		// Æ÷Ä¿½ºÁØ RowÀÇ Ã¹¹øÂ° Cell¿¡ Æ÷Áö¼ÇÀ» µÐ´Ù.
	grdList.SetCellPos(0);
}


