/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/

function fcDelRow(obj) {

	//¼±ÅÃµÈ Row Count
	var iSelectRowCount = fnc_GetColumnValueCount(dsList, "CHK", "1");

	if ( iSelectRowCount <= 0 ) {
		// »èÁ¦ÇÒ ÀÚ·á°¡ ¾ø½À´Ï´Ù.
		fnc_Message(SNIS_COM_1003, "N");
		return false;
	}

	var nRow = dsList.row;
	var strInDt = dsList.GetColumn(nRow, "IN_DT");

	if ( strInDt < substr(getDate(), 0, 8)) {
		// ±ÝÀÏµ¥ÀÌÅÍ¸¸ »èÁ¦ °¡´ÉÇÕ´Ï´Ù.
		fnc_Message(SNIS_RBM_B009, "N", " »èÁ¦ ");
		return;

	} 

	// ¼±ÅÃµÈ ÀÚ·á [ @ ]°Ç¸¦ »èÁ¦ ÇÏ½Ã°Ú½À´Ï±î?
	var bResult = fnc_Message(SNIS_COM_1010, "Y", iSelectRowCount);
	if ( bResult ) {
		fnc_DeleteRow(dsList, "CHK", "1");
	}

	return bResult;
}


