/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
// 부품명 조회
function getComboschPartsNm() {

	var sServiceName = "rbo4040-service:searchSchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSchP=dsSchP ";     

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


// 신청가능수량 조회
function getSchStkCnt() {

    var startDt = substr(calStartDate.Value, 0, 8);
    var endDt   = substr(calEndDate.Value, 0, 8);

    if ( startDt > endDt ) {		// 날짜 유효성 체크
		// 저장 할 자료가 없음
		fnc_Message(SNIS_RBM_E002, "N");
		return;
    }

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo4040-service:searchStkCnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsStkCnt=dsStkCnt ";     

	fcd_AddParam("PARTS_LCD"	, cboPartsLcd.Value			 );		// 발매기코드
	fcd_AddParam("PARTS_MCD"	, cboPartsMcd.Value			 );		// 부품그룹코드
	fcd_AddParam("PARTS_CD"		, cboPartsCd.Value			 );		// 부품코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*************************************************************************************/

function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}



// 출고신청 (승인요청전) 상단
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo4040-service:searchAprvList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsAprvList=dsAprvList ";     

    fcd_AddParam("BRNC_CD"		, gdsUser.GetColumn(0, "BRNC_CD")	);		// 지점코드(로그인한 유저의 지점)
	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value			 	);		// 발매기코드
	fcd_AddParam("PARTS_MCD"	, cboSchPartsMcd.Value			 	);		// 부품그룹코드
	fcd_AddParam("PARTS_CD"		, cboSchPartsCd.Value			 	);		// 부품코드
	fcd_AddParam("REQ_DT_FROM"	, substr(calStartDate.Value, 0, 8)	);		// 신청 시작 기간
	fcd_AddParam("REQ_DT_TO"	, substr(calEndDate.Value, 0 ,8)	);		// 신청 종료 기간
	fcd_AddParam("APRV_STAS"	, cboSchAprvStat.Value			 	);		// 상태


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}



/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  
	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsAprvList)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsAprvList;
	sMandatoryColID   = "BRNC_CD|PARTS_LCD|PARTS_MCD|PARTS_CD|MADE_GBN|REQ_CNT";
	sMandatoryColName = "지점|발매기|부품모델분류|부품명|구분|신청수량";
	oGrid             = grdAprvList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}



// 승인요청 버튼 클릭
function fcBtnOnClick(obj)
{	
	switch( obj.Id ) {
		// 발매기
		case "btnAprvReq" :
			var nRow = dsAprvList.row;
			var nChkCnt = 0;

			for(var i=0; i < dsAprvList.rowcount; i++) {
				if( dsAprvList.GetColumn(i, "CHK") == "1" ) {
					//승인상태가 '001'이 아니라면(승인요청전) 
					if( Length(dsAprvList.GetColumn(i, "APRV_STAS")) > 0 && dsAprvList.GetColumn(i, "APRV_STAS") != "001" ) {
						fnc_Message(SNIS_RBM_B015, "N", i+1);	
						return;
					}

					//신청수량이 0보다 클때만 신청 가능
					if( dsAprvList.GetColumn(i, "REQ_CNT") <= 0 ) {
						fnc_Message(SNIS_RBM_B016, "N", i+1);	
						return;
					}

					if( dsAprvList.GetColumn(i, "REQ_CNT") > dsAprvList.GetColumn(i, "STK_CNT")) {
						//[ @ ]줄의 [@]은(는) [@]보다 작아야합니다.
						fnc_Message(SNIS_RBM_B021, "N", i+1, "신청수량", "신청가능수량");
						return;
					}
					nChkCnt++;
				} else {
					if( dsAprvList.GetRowType(i) != "normal" ) {
						fnc_Message(SNIS_RBM_B019, "N", i+1);
						return;
					}
				}
			}

			if( nChkCnt == 0 ) {
				//	[ @ ] 처리할 데이터가 없습니다.
				fnc_Message(SNIS_RBM_B017, "N", "승인요청");
				return;
			}

			//	[ @ ]하시겠습니까?
			if(fnc_Message(SNIS_RBM_F032, "Y", "승인요청")) { 			
				fcSaveData("002");
			}		

			break;

		case "btnAprvReqCancel":
			for(var i=0; i < dsAprvList.rowcount; i++) {
				if( dsAprvList.GetColumn(i, "CHK") == "1" ) {
					//승인상태가 '002'이 아니라면(승인요청) 
					if( Length(dsAprvList.GetColumn(i, "APRV_STAS")) > 0 && dsAprvList.GetColumn(i, "APRV_STAS") != "002" ) {
						fnc_Message(SNIS_RBM_B027, "N", i+1);	
						return;
					}
				}
			}
			if(fnc_Message(SNIS_RBM_F032, "Y", "회수")) { 			
				fcSaveData("001");
			}					

			break;
    }


}

function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

 	var nRow = dsAprvList.row;
	var nChkCnt = 0;

	for(var i=0; i < dsAprvList.rowcount; i++) {
			//신청수량이 0보다 클때만 신청 가능
			if( dsAprvList.GetColumn(i, "REQ_CNT") <= 0 ) {
				fnc_Message(SNIS_RBM_B016, "N", i+1);	
				return;
			}

			if( dsAprvList.GetColumn(i, "REQ_CNT") > dsAprvList.GetColumn(i, "STK_CNT")) {
				//[ @ ]줄의 [@]은(는) [@]보다 작아야합니다.
				fnc_Message(SNIS_RBM_B021, "N", i+1, "신청수량", "신청가능수량");
				return;
			}

	}


	for(var i=0; i < dsAprvList.rowcount; i++) {
		if( dsAprvList.GetColumn(i, "CHK") == "1" ) {
			dsAprvList.SetColumn(i, "REQ_DT"   , substr(getDate(), 0, 8));		// 신청일자
			dsAprvList.SetColumn(i, "APRV_STAS", "001"				    );		// 승인요청
		} 
	}


    var sServiceName = "rbo4040-service:SavePartsOutReq";
	var sInDataSet   = "dsAprvList=dsAprvList:u ";
	var sOutDataSet  = "dsAprvList=dsAprvList ";

	fcd_AddParam("BRNC_CD", gdsUser.GetColumn(0, "BRNC_CD")	);		// 지점코드(로그인한 유저의 지점)

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}




// 승인요청 클릭 시 처리
function fcSaveData(AprvStas) {    

	for(var i=0; i < dsAprvList.rowcount; i++) {
		if( dsAprvList.GetColumn(i, "CHK") == "1" ) {
			dsAprvList.SetColumn(i, "REQ_DT"   , substr(getDate(), 0, 8));		// 신청일자
			dsAprvList.SetColumn(i, "APRV_STAS", AprvStas			    );		// 승인요청
		} 
	}

	if ( !fcSaveCheck() ) return;

    var sServiceName = "rbo4040-service:SavePartsOutReq";
	var sInDataSet   = "dsAprvList=dsAprvList:u ";
	var sOutDataSet  = "dsAprvList=dsAprvList ";

	fcd_AddParam("BRNC_CD", gdsUser.GetColumn(0, "BRNC_CD")	);		// 지점코드(로그인한 유저의 지점)

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


