/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	// 부품목록 조회 완료 후
	if ( sServiceName == "rbo4040-service:searchSchParts") {

		/* 등록에 해당하는 필터링 */
		dsCd.Copy(dsSchP);			// 부품 (등록 콤보)

		// Combo 에 전체 추가 : 발매기, 부품분류, 부품
		dsSchP.fireEvent = false;
		dsSchP.InsertRow(0);		
		dsSchP.SetColumn(0,"PARTS_CD", "" );
		dsSchP.SetColumn(0,"PARTS_NM", "전체");
		dsSchP.fireEvent = true;
		cboSchPartsCd.Index = 0;
	}

		// 조회 완료 후
	if ( sServiceName == "rbo4040-service:searchStkCnt" ) {

	    if(dsStkCnt.GetRowCount() > 0){
			edStkCnt.Text = dsStkCnt.GetColumn(0, "STK_CNT");
		}else{
			edStkCnt.Text = "0";
		}
	}	


	// 조회 완료 후
	if ( sServiceName == "rbo4040-service:searchList" ) {
		if( dsList.rowcount == 0 )	btnAprvReq.Visible = false;

		fcSearchAprv();
	}			


	// 저장 완료 후
	if ( sServiceName == "rbo4040-service:SavePartsOutReq" ) {
		// 쪽지 전송
		//fcSendMngMsg();

		fcSearch();		
	}	
}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	if(gdsUser.GetColumn(0, "BRNC_CD") == "00"){
		fnc_Message(SNIS_RBM_B023,"N");		

		return;
	}else{
		/*


		// 로그인한 유저 지점코드 및 신청날짜 셋팅
		dsAprvList.SetColumn(nRow, "BRNC_CD"  , gdsUser.GetColumn(0, "BRNC_CD"));
		dsAprvList.SetColumn(nRow, "REQ_DT"   , substr(getDate(),0,8));
		dsAprvList.SetColumn(nRow, "APRV_STAS", "001");
		//dsAprvList.SetColumn(nRow, "PARTS_LCD", "");

		*/

		var sFormUrl 	= "";
		var sArgs 		= "";
		var arrReturn   = array(4);

		sFormUrl = "frm_rbo::RBO4041.xml";

		sArgs = "";

		arrReturn = Dialog(sFormUrl, sArgs,712,512, "",220,150);

		if(arrReturn.length <=0) return;

		var arr   = array();
		var bIsExist = false;
		for(var i = 0; i < arrReturn.length;i++){

			arr = Split(arrReturn[i],"|");

			bIsExist = false;


			for(var j=0;j <= dsAprvList.GetRowCount();j++){
				if(dsAprvList.GetRowType(j) == "insert" && dsAprvList.GetColumn(j,"PARTS_CD") == arr[2] ){

					fnc_Message(SNIS_RBM_B025,"N");

					bIsExist = true;
				}

			}

			if(!bIsExist){
				var nRow = dsAprvList.AddRow();


				dsAprvList.SetColumn(nRow, "BRNC_CD"  , gdsUser.GetColumn(0, "BRNC_CD"));
				dsAprvList.SetColumn(nRow, "REQ_DT"   , substr(getDate(),0,8));
				dsAprvList.SetColumn(nRow, "APRV_STAS", "001");


				dsAprvList.SetColumn(nRow, "PARTS_LCD"  , arr[0]);
				dsAprvList.SetColumn(nRow, "PARTS_MCD"  , arr[1]);
				dsAprvList.SetColumn(nRow, "PARTS_CD"  	, arr[2]);
				dsAprvList.SetColumn(nRow, "STK_CNT"  	, arr[3]);
				dsAprvList.SetColumn(nRow, "MADE_GBN"  	, arr[4]);

			}


		}



	 }
}

