function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{
	switch( obj.id ) {
		case "dsAprvList" :
			if( obj.GetColumn(nRow, "APRV_STAS") == "001" || Length(obj.GetColumn(nRow, "APRV_STAS")) ==0 ) {
				edReqCnt.Enable = true;
				edReqRsn.Enable = true;

				cboPartsLcd.Enable = true;
				cboPartsMcd.Enable = true;
				cboPartsCd.Enable = true;

				edReqCnt.SetFocus();

			} else {
				edReqCnt.Enable = false;
				edReqRsn.Enable = false;

				cboPartsLcd.Enable = false;
				cboPartsMcd.Enable = false;
				cboPartsCd.Enable = false;
			}
		break;
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch( obj.Id ) {
		// 발매기
		case "cboSchPartsLcd" :
			var strFilter  = "";

			if( strCode == "001") strFilter = "CD_TERM1=='001'||CD==''";
			if( strCode == "002") strFilter = "CD_TERM2=='002'||CD==''";
			if( strCode == "003") strFilter = "CD_TERM3=='003'||CD==''";	

			dsPartsMcd.Filter(strFilter);	//부품명 Filter
			cboSchPartsMcd.Index = 0;

			dsSchP.Filter("");
			cboSchPartsCd.Index = 0;

			fcSearch();
		break;

		// 부품그룹
		case "cboSchPartsMcd" :
			if( length(strCode) > 0){
				var strFilter = "";
				if( fnc_IsNull(cboSchPartsLcd.Value)) {
					//발매기 전체일시
					strFilter="PARTS_MCD=='"+strCode+"'||PARTS_CD==''";	
				} else {
					//발매기 선택시
					strFilter="PARTS_LCD=='"+cboSchPartsLcd.Value+"'&&PARTS_MCD=='"+strCode+"'||PARTS_CD==''";	
				}

				dsSchP.Filter(strFilter);
				cboSchPartsCd.Index 	= 0;
			} else {
				dsSchP.Filter("");
				cboSchPartsCd.Index = 0;
			}
			fcSearch();
		break;
		case "cboSchPartsCd" :	
			fcSearch();
		break;
		case "cboSchAprvStat" :
			fcSearch();
		break;
	}
}

function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.Id ) {
		// 발매기
		case "dsPartsLcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.fireEvent = true;
				cboSchPartsLcd.Index = 0;
			}
		break;

		// 부품그룹
		case "dsPartsMcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.setColumn(0,"CD_TERM1","001");
				obj.setColumn(0,"CD_TERM2","002");
				obj.setColumn(0,"CD_TERM3","003");
				obj.fireEvent = true;
				cboSchPartsMcd.Index = 0;
			}
		break;
		// 상태
		case "dsAprvStasSearch" :
			obj.insertRow(0);
			obj.setColumn(0,"CD_NM","전체");


			cboSchAprvStat.value = "002";
		break;
	}
}


