	  for(var i = 0; i <dsAlarm.GetRowCount();i++){

			 fcd_SendMessage(sSendUser			// 송신자 사번 
			, dsAlarm.GetColumn(i,"USER_ID") 	// 수신자 사번 	
			, sTitle	    			// 제목
			, "RBO4040" 				// 링크 주소
			,  sDesc					// 내용
			, "comSendMsg"				// 컴포넌트 ID
			);								
	  }
}
