function fcInit(obj) {
    dsList.ClearData();  
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}

// 부품 목록 조회
function getComboschPartsNm() {
	var sServiceName = "rbo4040-service:searchSchParts";
    var sInDataSet   = "";
    var sOutDataSet  = "dsSchP=dsSchP ";     

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

// dsList 조회
function fcSearch(obj) {

    var startDt = substr(calStartDate.Value, 0, 8);
    var endDt   = substr(calEndDate.Value  , 0, 8);

    if ( startDt > endDt ) {		// 날짜 유효성 체크
		fnc_Message(SNIS_RBM_E002, "N");
		return;
    }

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo4050-service:searchList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("BRNC_CD"		, cboSchBrncCd.Value	);		// 지점코드
	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value	);		// 발매기 코드
	fcd_AddParam("REQ_DT_FROM"	, calStartDate.Value	);		// 신청 시작 기간
	fcd_AddParam("REQ_DT_TO"	, calEndDate.Value		);		// 신청 종료 기간
	fcd_AddParam("APRV_STAS"	, cboSchAprvYn.Value	);		// 승인상태

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "APRV_STAS|APRV_CNT";
	sMandatoryColName = "승인상태|승인수량";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	} 	
	return true;
}


function fcSaveData() {

	dsAlarmList.ClearData();

	// 반려일 때 승인 수량에 0 세팅.
	for( var i=0; i<dsList.rowcount; i++) {
		if( dsList.GetColumn(i, "CHK") == "1" ) {


			if( stasFlag == "NO" ) {
				dsList.SetColumn(i, "APRV_CNT", "0");
			}

			var nRow = dsAlarmList.AddRow();
			dsAlarmList.CopyRow(nRow,dsList,i);
		}
	}


	if ( !fcSaveCheck() ) return;


	for(var i=0; i < dsList.rowcount; i++) {
		if( dsList.GetColumn(i, "CHK") == "1" &&  dsList.GetColumn(i, "APRV_STAS") == "002" ) {
				dsList.SetColumn(i, "APRV_CNT", "0");
		} 
	}



	var sServiceName = "rbo4050-service:SavePartsOutAppr";
	var sInDataSet   = "dsList=dsList:u ";
	var sOutDataSet  = "dsList=dsList";	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

