/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbo4050-service:searchList" ) {

	}			

	// 저장 완료 후
	if ( sServiceName == "rbo4050-service:SavePartsOutAppr" ) {
		// 쪽지 전송
		fcSendMngMsg();

		fcSearch();		
	}	
}

