// 승인처리 관련 버튼 제어
function fcButton_OnClick(obj)				// 승인신청시 (002)
{
	var nRow = dsList.row;

	if( obj.ID == "btnCancleAprv" ) {		// 승인취소 (005)

		//제약조건
		var nChkCnt = 0;

		for(var i=0; i < dsList.rowcount; i++) {
			if( dsList.GetColumn(i, "CHK") == "1" ) {

				//승인상태 Check
				if( dsList.GetColumn(i, "APRV_STAS") != "003" ) {
					fnc_Message(SNIS_RBM_B020, "N", i+1, "승인취소");
					return;
				}

				//입고여부 Check
				if( dsList.GetColumn(i, "BRNC_IN_YN") == "Y" ) {
					fnc_Message(SNIS_RBM_B024, "N", i+1, "승인취소");
					return;
				}
				nChkCnt++;
			} else {
				if( dsList.GetRowType(i) != "normal" ) {
					fnc_Message(SNIS_RBM_B019, "N", i+1);
					return;
				}
			}
		}

		if( nChkCnt == 0 ) {
			fnc_Message(SNIS_RBM_B017, "N", "승인취소");
			return;
		}

		//승인처리
		if( fnc_Message(SNIS_RBM_F032, "Y", "승인취소") ) {
			for(var i=0; i < dsList.rowcount; i++) {
				if( dsList.GetColumn(i, "CHK") == "1" ) {
					dsList.SetColumn(i, "APRV_STAS"    , "002");
					dsList.SetColumn(i, "APRV_DT"      , "");
					dsList.SetColumn(i, "APRV_OFIR_NO" , "");
					dsList.SetColumn(i, "APRV_CNT" , "0");
				} 
			}

		fcd_AddParam("APRV_STAS_CHG", "003_002");

		btnAprv.Visible = true;			// 승인 버튼
		btnCancleAprv.Visible = false;	// 승인취소 버튼
		btnReject.Visible = false;		// 반려 버튼
		edtAprvCnt.Enable = true;

		stasFlag = "CANCEL";

		fcSaveData();
	  }
	} else if ( obj.ID == "btnAprv" ) {		// 승인 (003)
		//제약조건
		var nChkCnt = 0;

		for(var i=0; i < dsList.rowcount; i++) {
			if( dsList.GetColumn(i, "CHK") == "1" ) {

				//승인상태 Check
				if( dsList.GetColumn(i, "APRV_STAS") != "002" ) {
					fnc_Message(SNIS_RBM_B020, "N", i+1, "승인");
					return;
				}

				//승인수량 Check
				if( dsList.GetColumn(i, "APRV_CNT") <= 0 ) {
					fnc_Message(SNIS_RBM_B018, "N", i+1);
					return;
				}

				//승인수량 Check
				if( dsList.GetColumn(i, "REQ_CNT") < dsList.GetColumn(i, "APRV_CNT")) {
					fnc_Message(SNIS_RBM_B021, "N", i+1,"승인수량","신청수량");
					return;
				}

				//승인수량 Check
				if( dsList.GetColumn(i, "STK_CNT") < dsList.GetColumn(i, "APRV_CNT")) {
					fnc_Message(SNIS_RBM_B021, "N", i+1,"승인수량","현재고수량");
					return;
				}



				nChkCnt++;
			} else {
				if( dsList.GetRowType(i) != "normal" ) {
					fnc_Message(SNIS_RBM_B019, "N", i+1);
					return;
				}
			}
		}

		if( nChkCnt == 0 ) {
			fnc_Message(SNIS_RBM_B017, "N", "승인");
			return;
		}

		//승인처리
		if( fnc_Message(SNIS_RBM_F032, "Y", "승인") ) {
			for(var i=0; i < dsList.GetRowCount(); i++) {
				if( dsList.GetColumn(i, "CHK") == "1" ) {
					dsList.SetColumn(i, "APRV_STAS"    , "003");
					dsList.SetColumn(i, "APRV_DT"      , substr(getDate(), 0, 8));
					dsList.SetColumn(i, "APRV_OFIR_NO" , gdsUser.GetColumn(0, "USER_ID") );
				} 
			}

			fcd_AddParam("APRV_STAS_CHG", "002_003");

			btnAprv.Visible = false;		// 승인 버튼
			btnCancleAprv.Visible = true;	// 승인취소 버튼
			btnReject.Visible = false;		// 반려 버튼
			edtAprvCnt.Enable = false;

			stasFlag = "OK";

			fcSaveData();
		}
	} else if ( obj.ID == "btnReject" ) {	// 반려 (004)

		//제약조건
		var nChkCnt = 0;

		for(var i=0; i < dsList.rowcount; i++) {
			if( dsList.GetColumn(i, "CHK") == "1" ) {

				//승인상태 Check
				if( dsList.GetColumn(i, "APRV_STAS") != "002" ) {
					fnc_Message(SNIS_RBM_B020, "N", i+1, "반려");
					return;
				}
				nChkCnt++;
			} else {
				if( dsList.GetRowType(i) != "normal" ) {
					fnc_Message(SNIS_RBM_B019, "N", i+1);
					return;
				}
			}
		}

		if( nChkCnt == 0 ) {
			fnc_Message(SNIS_RBM_B017, "N", "반려");
			return;
		}

		//반려처리
		if( fnc_Message(SNIS_RBM_F032, "Y", "반려") ) {
			for(var i=0; i < dsList.rowcount; i++) {
				if( dsList.GetColumn(i, "CHK") == "1" ) {
					dsList.SetColumn(i, "APRV_STAS", "004");
					dsList.SetColumn(i, "APRV_CNT" , "0");
				} 
			}

			fcd_AddParam("APRV_STAS_CHG", "002_001");

			btnAprv      .Visible = false;	// 승인 버튼
			btnCancleAprv.Visible = false;	// 승인취소 버튼
			btnReject    .Visible = false;	// 반려 버튼
			edtAprvCnt.Enable = true;

			stasFlag = "NO";

			fcSaveData();
		}
	}
}


function dsList_OnRowPosChanged(obj,nOldRow,nRow)
{
	//이미지 보여주기
	var sFileNm = dsList.GetColumn(nRow, "REAL_FILE_NM");

	if( length(sFileNm) > 0 ) {
		img1.ImageID = GBL_SERVER_UPLOAD_DIR + SubStr(GBL_MENUID,0,3) + "/" + sFileNm;
	} else {
		img1.ImageID = "";
	}


	var strAprvStas = dsList.GetColumn(nRow, "APRV_STAS");

	if( strAprvStas == "003" ) {	// 승인일 경우,

		btnAprv.Visible = false;		// 승인 버튼
		btnCancleAprv.Visible = true;	// 승인취소 버튼
		btnReject.Visible = false;		// 반려 버튼
		edtAprvCnt.Enable = false;

	} else if( strAprvStas == "001" ) {		// 승인요청전 일 경우,

		btnAprv.Visible = false;		// 승인 버튼
		btnCancleAprv.Visible = false;	// 승인취소 버튼
		btnReject.Visible = false;		// 반려 버튼
		edtAprvCnt.Enable = false;

	} else if( strAprvStas == "004" ) {		// 승인요청전 일 경우,

		btnAprv.Visible = false;		// 승인 버튼
		btnCancleAprv.Visible = false;	// 승인취소 버튼
		btnReject.Visible = false;		// 반려 버튼
		edtAprvCnt.Enable = false;


	} else {	// 승인요청일 경우,

		btnAprv.Visible = true;			// 승인 버튼
		btnCancleAprv.Visible = false;	// 승인취소 버튼
		btnReject.Visible = true;		// 반려 버튼
		edtAprvCnt.Enable = true;
	}
}


// 쪽지 전송 ( 승인 or 반려 )
function fcSendMngMsg(){

	  var sSendUser = gdsUser.GetColumn(0,"USER_ID");
	  var sRecvUser = "";
	  var sTitle	= "출고승인여부";
	  var sDesc		= "";

	  for(var i = 0; i <dsAlarmList.GetRowCount();i++){

		     sRecvUser += dsAlarmList.GetColumn(i,"REQ_NO")+",";

		     sDesc	= "승인상태 : " +dsAprvStas.LookUp("CD",dsAlarmList.GetColumn(i,"APRV_STAS"),"CD_NM") ;


	  }		

	   if(Length(sRecvUser) >0){

		   sRecvUser =sRecvUser.SubStr(0,Length(sRecvUser)-1);

		   fcd_SendMessage(sSendUser	// 송신자 사번 
						, sRecvUser	// 수신자 사번 
						, sTitle	    // 제목
						,  "RBO4050" 	// 수신자 번호
						, sDesc		// 내용
						, "comSendMsg"
						);

	   }
}


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{


	switch ( obj.Id ) {

		case "cboSchPartsLcd" :
			fcSearch();
		break;

		case "cboSchAprvYn" :
			fcSearch();
		break;

		case "cboSchBrncCd" :
			fcSearch();
		break;
	}
}
