	// ºÎÇ°¸ñ·ÏÁ¶È¸	
	http.sync = true;
	getComboschPartsNm();
	http.sync = false;

	// ÁöÁ¡ÄÚµå 'ÀüÃ¼' Ãß°¡
	dsBrncCd.InsertRow(0);		
	dsBrncCd.SetColumn(0,"CD"   , "" 		);
	dsBrncCd.SetColumn(0,"CD_NM", "ÀüÃ¼"	);
	cboSchBrncCd.Index = 0;

	//µ¥ÀÌÅÍ¼Â º¹»ç( Á¶È¸Á¶°Ç¿ë / ÀÔ·Â¿ë )
	//dsLcd.Copy(dsPartsLcd);
	//dsMcd.Copy(dsPartsMcd);
	//dsCd .Copy(dsSchP); 

