// dsList Á¶È¸
function fcSearch(obj) {

    var startDt = substr(calStartDate.Value, 0, 8);
    var endDt = substr(calEndDate.Value, 0, 8);

    if ( startDt > endDt ) {		// ³¯Â¥ À¯È¿¼º Ã¼Å©

		// ÀúÀå ÇÒ ÀÚ·á°¡ ¾øÀ½
		fnc_Message(SNIS_RBM_E002, "N");
		return;
    }


    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbo4070-service:searchList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("BRNC_CD"		, cboSchBrncCd.Value				);		// ÁöÁ¡ÄÚµå
	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value				);		// ¹ß¸Å±â ÄÚµå
	fcd_AddParam("PARTS_MCD"	, cboSchPartsMcd.Value				);		// ºÎÇ°±×·ìÄÚµå
	fcd_AddParam("PARTS_CD"		, cboSchPartsCd.Value				);		// ºÎÇ°ÄÚµå
	fcd_AddParam("OUT_DT_FROM"	, substr(calStartDate.Value, 0, 8)	);		// Ãâ°í ½ÃÀÛ ±â°£
	fcd_AddParam("OUT_DT_TO"	, substr(calEndDate.Value, 0, 8)	);		// Ãâ°í Á¾·á ±â°£
	fcd_AddParam("APRV_STAS"	, cboSchAprvYn.Value				);		// ½ÂÀÎ»óÅÂ	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}


