/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTFFTTT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);

	//보유현황 메뉴에서 이동되어 진 경우 기간에 관계없이 조회 
	if(Length(sRetValue)==0){

		calStartDate.Value = substr(getDate(), 0, 4) + "0101";	// 현재달의 첫날(매월 1일)
		calEndDate.Value = getDate();

	}



	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);


	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

	//부품명 조회
	getComboschPartsNm();

    //타 화면에서 이동되어진 경우 넘어온 검색조건 처리     
	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";

		cboSchPartsLcd.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"PARTS_LCD");
		cboSchPartsMcd.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"PARTS_MCD");


		sSrchPartCd = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"PARTS_CD");
		sSrchPartMCd = fcArray2Value(ARRAY2_GBL_RET_VALUE,"PARTS_MCD");
		cboSchPartsCd.Value = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"PARTS_CD");

	}

    // 목록 조회 
    fcSearch();  
}


/*-------------------------------------------------------------------------------------------------+
>>  화면 실행할 때마다 실행 (타메뉴에서 이동되어지는 화면일 경우 필수 FUNCTION )
+-------------------------------------------------------------------------------------------------*/
function fcFormOnActivite(){
	var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);
	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";

		cboSchPartsLcd.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"PARTS_LCD");
		cboSchPartsMcd.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,"PARTS_MCD");


		sSrchPartCd = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"PARTS_CD");
		sSrchPartMCd = fcArray2Value(ARRAY2_GBL_RET_VALUE,"PARTS_MCD");
		cboSchPartsCd.Value = fnc_Array2Value(ARRAY2_GBL_RET_VALUE,"PARTS_CD");

		fcSearch();  
	}
}



