/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearch(obj) {

	var sServiceName = "rbo4120-service:searchInOutList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsInOutList=dsInOutList ";

    var sInOutGbn = "";
    if(rdoInOutGbn.Value == "000") sInOutGbn="";
    else sInOutGbn=rdoInOutGbn.Value;




    fcd_AddParam("INOUT_FROM"	, calStartDate.Value	);		// 
	fcd_AddParam("INOUT_TO"		, calEndDate.Value		);		// 
	fcd_AddParam("BRNC_CD"		, cboSchBrncCd.Value	);		// 
	fcd_AddParam("INOUT_GBN"	, sInOutGbn);					// 
	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value);		// 

	if(Length(sSrchPartCd)>0){

		fcd_AddParam("PARTS_MCD",sSrchPartMCd);		// 
		fcd_AddParam("PARTS_CD"	, sSrchPartCd);				// 



		//화면이동 구분 위해 반드시 !! 필요 !!(sSrchPartCd으로 비교 )
		sSrchPartCd="";

	}else{
		fcd_AddParam("PARTS_MCD", cboSchPartsMcd.Value);		// 
		fcd_AddParam("PARTS_CD"	, cboSchPartsCd.Value);				// 

	}



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


