/*-------------------------------------------------------------------------------------------------+
>>  프린트 인쇄 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {

	//데이터셋을 초기화 한다.
	ds_reportParam.DeleteAll();
	var nRnum;

    fcd_AddParam("INOUT_FROM"	, calStartDate.Value	);		// 
	fcd_AddParam("INOUT_TO"		, calEndDate.Value		);		// 
	fcd_AddParam("BRNC_CD"		, cboSchBrncCd.Value	);		// 
	fcd_AddParam("INOUT_GBN"	, sInOutGbn);					// 
	fcd_AddParam("PARTS_LCD"	, cboSchPartsLcd.Value);		// 


    // 필드 값을 셋팅한다.
    var paramField = [
							"INOUT_FROM"						// 조회시작기간
						,	"INOUT_TO"							// 조회종료기간
						,	"BRNC_CD"							// 지점코드
						,	"INOUT_GBN"							// 입출구분
						,	"PARTS_LCD"							// 부품그룹코드
					 ];

	// 값을 셋팅한다.
	var paramValue = [
							calStartDate.Value
						,	calEndDate.Value
						,	cboSchBrncCd.Value
						,	sInOutGbn
						,	cboSchPartsLcd.Value
					 ];


	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	fnc_ReportPreView("rbo4120", ds_reportParam.ID, "N");

}


