		dsSchP.fireEvent = true;

		//화면이동 구분 위해 반드시 !! 필요 !! (sSrchPartMCd으로 비교 )
		if(Length(sSrchPartMCd)>0){

		}else{
			cboSchPartsCd.Index = 0;
		}

	}


}


