/***************** 20111130 추가 *********************/

function fcCombo_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch( obj.Id ) {
		case "cboSchPartsLcd" :
			if( length(strCode) > 0){
				var strFilter="";
				if( strCode == "001") strFilter = "CD_TERM1=='001'";
				if( strCode == "002") strFilter = "CD_TERM2=='002'";
				if( strCode == "003") strFilter = "CD_TERM3=='003'";		
				dsPartsMcd.Filter(strFilter);
				cboSchPartsMcd.Enable 	= true;
				cboSchPartsMcd.Index 	= 0;
			}
			else{
				strFilter = "CD_TERM1=='001'";
				dsPartsMcd.Filter(strFilter);
				cboSchPartsMcd.Enable 	= true;
				cboSchPartsMcd.Index 	= 0;
			}
			fcSearch();
		break;
		case "cboSchPartsMcd" :
			fcSearch();
		break;
		case "cboSchBrncCd" :
			fcSearch();
		break;
	}
}


// 발매기 '전체' 삽입
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.Id ) {
		case "dsBrncCd" :
			if( nReason == 0){
					obj.fireEvent = false;
					obj.insertRow(0);
					obj.setColumn(0,"CD","");
					obj.setColumn(0,"CD_NM","전체");
					obj.fireEvent = true;
					cboSchBrncCd.Index = 0;
			}
		break;

			// 발매기
		case "dsPartsLcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )
				//dsLcd.Copy(obj);

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.fireEvent = true;
				cboSchPartsLcd.Index = 0;
			}
		break;

		// 부품그룹
		case "dsPartsMcd" :

			if( nReason == 0){
				//데이터셋 복사( 조회조건용 / 입력용 )
				//dsMcd.Copy(obj);

				obj.fireEvent = false;
				obj.insertRow(0);
				obj.setColumn(0,"CD","");
				obj.setColumn(0,"CD_NM","전체");
				obj.setColumn(0,"CD_TERM1","001");
				obj.setColumn(0,"CD_TERM2","002");
				obj.setColumn(0,"CD_TERM3","003");
				obj.fireEvent = true;

				if(Length(sSrchPartMCd)>0){
					sSrchPartMCd="";
				}else{
					cboSchPartsMcd.Index = 0;
				}

			}
		break;

	}
}
