function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.Id ) {
		case "cboSchBrncCd" :
			fcSearch();
		break;

		case "cboSchPartsLcd" :
			var strFilter  = "";

			if( strCode == "001") strFilter = "CD_TERM1=='001'";
			if( strCode == "002") strFilter = "CD_TERM2=='002'";
			if( strCode == "003") strFilter = "CD_TERM3=='003'";		

			dsPartsMcd.Filter(strFilter);	//부품명 Filter
			cboSchPartsMcd.Index = 0;

			dsSchP.Filter("");
			cboSchPartsCd.Index = 0;
			fcSearch();
		break;

		// 부품그룹
		case "cboSchPartsMcd" :
			if( length(strCode) > 0){
				var strFilter = "";
				if( fnc_IsNull(cboSchPartsLcd.Value)) {
					//발매기 전체일시
					strFilter="PARTS_MCD=='"+strCode+"'||PARTS_CD==''";	
				} else {
					//발매기 선택시
					strFilter="PARTS_LCD=='"+cboSchPartsLcd.Value+"'&&PARTS_MCD=='"+strCode+"'||PARTS_CD==''";	
				}

				dsSchP.Filter(strFilter);
				cboSchPartsCd.Index 	= 0;
			} else {
				dsSchP.Filter("");
				cboSchPartsCd.Index = 0;
			}

			fcSearch();
		break;

		case "cboSchPartsCd" :
			fcSearch();
		break;

   }
}

function fcRdoOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
   switch ( obj.Id ) {
		case "rdoInOutGbn" :
			fcSearch();
		break;
   }
}




