// 2차원 배열의 변수 값 확인
function fcArray2Value(arr2Param,strParam)
{
	var strResult = "";
	for(var i=0;i<arr2Param.length;i++)
	{	
		for(var j=0;j<arr2Param[i].length;j++)
		{
			if(arr2Param[i][0]==strParam)
			{
				strResult = arr2Param[i][1];
			}
		}
	}
	return strResult;
}
