/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset;

	//일반사항이 insert일 경우에는 컬럼값이 실제로 변경이 있을 경우에만 실행시켜준다 
	if( dsCommDesc.GetRowType(dsCommDesc.currow) = "insert" ) 
	{
		sDataset = "dsFloorMana";

		if( !fcDsChangeCheck() ) {
			//화면 종료 여부 확인
			if ( !fnc_Message(SNIS_COM_1001, "Y") ) {
				return true;
			}
		}

	} else {
		sDataset = "dsCommDesc|dsFloorMana";
	}

	if ( !fnc_FormUnloadCheck(sDataset, sFormId) )	return true;
}


