function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	//일반사항, 층관리
	var sServiceName = "rbr1011-service:searchCommDesc";
	var sInDataSet   = "";
	var sOutDataSet  = "dsCommDesc=dsCommDesc dsFloorMana=dsFloorMana";		

	fcd_AddParam("BRNC_CD"  , parent.cboBrncCdSearch.Value);
	fcd_AddParam("STND_YEAR", parent.cboStndYearSearch.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	// 필수항목체크
	oDataSet          = dsFloorMana;
	sMandatoryColID   = "FLOOR";
	sMandatoryColName = "층";
	oGrid             = grdFloorMana;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}
	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return false;  

	var sServiceName = "rbr1011-service:saveCommDesc";
	var sInDataSet   = "dsCommDesc=dsCommDesc:u dsFloorMana=dsFloorMana:u";
	var sOutDataSet  = "dsCommDesc=dsCommDesc dsFloorMana=dsFloorMana"; 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	return true;
}

