/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_ResultNP(ErrorCode, ErrorMSG) ) return;

	// 조회 완료 후
	if ( sServiceName == "rbr1011-service:searchCommDesc" ) {
		if( dsCommDesc.rowcount == 0 ) {
			//일반사항에 조회내용이 없을 경우, 신규로 생성
			var nRow = dsCommDesc.AddRow();

			//자동 Setting
			dsCommDesc.SetColumn(nRow, "BRNC_CD"  , parent.cboBrncCdSearch.Value);
			dsCommDesc.SetColumn(nRow, "STND_YEAR", parent.cboStndYearSearch.Value);
			mskSalesTotal.Value = "0";
			mskSalesTotalGreen.Value = "0";

		} else {
			// 매출합계 계산
			var nRow = dsCommDesc.row;
			mskSalesTotal.Value = dsCommDesc.GetColumn(nRow, "SALES_GOAL_CRA")
						        + dsCommDesc.GetColumn(nRow, "SALES_GOAL_MRA");
			mskSalesTotalGreen.Value = dsCommDesc.GetColumn(nRow, "SALES_GOAL_CRA_GREEN")
						        + dsCommDesc.GetColumn(nRow, "SALES_GOAL_MRA_GREEN");						        
		}
	}			

	// 저장 완료 후
	if ( sServiceName == "rbr1011-service:saveCommDesc" ) {
		//매출현황 조회 
		tab.tbSalesMana.fcSearch();

		fcSearch();		
	}	
}

