/*-------------------------------------------------------------------------------------------------+
>>  Column의 값이 변경된 경우
+-------------------------------------------------------------------------------------------------*/
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	switch (obj.id) {
		case "dsFloorMana" :	//일반사항, 층 관리
			//중복코드 체크(층)
			if ( strColumnID == "FLOOR" ) {
				if ( fnc_GetExistValue(obj, strColumnID, varNewValue) != nRow ) {
					fnc_Message(L_COM_ALT_0016, "N", "층이");
					obj.row = nRow;
					obj.SetColumn(nRow, strColumnID, varOldValue);
				}
				if ( fnc_GetExistValue(obj, strColumnID, varNewValue, nRow + 1) > 0 ) {
					fnc_Message(L_COM_ALT_0016, "N", "층이");
					obj.row = nRow;
					obj.SetColumn(nRow, strColumnID, varOldValue);
				}
			}

			// 평계산(공용면적) -> m2을 평으로 계산
			if( strColumnID == "COMM_AREA_SQMT" ) {
				dsFloorMana.SetColumn( nRow, "COMM_AREA_PY", fnc_Sqmt(varNewValue, 2) );

				dsFloorMana.SetColumn( nRow, "TOT_AREA_PY", 
											fnc_Sqmt(dsFloorMana.GetColumn(nRow,"COMM_AREA_SQMT"), 2) + fnc_Sqmt(dsFloorMana.GetColumn(nRow,"ONLY_AREA_SQMT"), 2));


				dsFloorMana.SetColumn( nRow, "TOT_AREA_SQMT", 
											dsFloorMana.GetColumn(nRow,"COMM_AREA_SQMT") + dsFloorMana.GetColumn(nRow,"ONLY_AREA_SQMT") );

				mskCommAreaSqmtTotal.Value =dsFloorMana.Sum("COMM_AREA_SQMT") ;
				mskCommAreaPyTotal.Value = dsFloorMana.Sum("COMM_AREA_PY") ;

				mskTotAreaSqmtTotal.Value = dsFloorMana.Sum("COMM_AREA_SQMT")  +  dsFloorMana.Sum("ONLY_AREA_SQMT");
				mskTotAreaPyTotal.Value = dsFloorMana.Sum("COMM_AREA_PY")  +  dsFloorMana.Sum("ONLY_AREA_PY");

			}

			// 평계산(전용면적) -> m2을 평으로 계산
			if( strColumnID == "ONLY_AREA_SQMT" ) {
				dsFloorMana.SetColumn( nRow, "ONLY_AREA_PY", fnc_Sqmt(varNewValue, 2) );

				dsFloorMana.SetColumn( nRow, "TOT_AREA_PY", 
											fnc_Sqmt(dsFloorMana.GetColumn(nRow,"COMM_AREA_PY"), 2) + fnc_Sqmt(dsFloorMana.GetColumn(nRow,"ONLY_AREA_PY"), 2));

				dsFloorMana.SetColumn( nRow, "TOT_AREA_SQMT", 
											dsFloorMana.GetColumn(nRow,"COMM_AREA_SQMT") + dsFloorMana.GetColumn(nRow,"ONLY_AREA_SQMT") );



				mskOnlyAreaSqmtTotal.Value =dsFloorMana.Sum("ONLY_AREA_SQMT") ;
				mskOnlyAreaPyTotal.Value = dsFloorMana.Sum("ONLY_AREA_PY") ;

				mskTotAreaSqmtTotal.Value = dsFloorMana.Sum("COMM_AREA_SQMT")  +  dsFloorMana.Sum("ONLY_AREA_SQMT");
				mskTotAreaPyTotal.Value = dsFloorMana.Sum("COMM_AREA_PY")  +  dsFloorMana.Sum("ONLY_AREA_PY");

			}
			break;
	}
}	

