/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	//Return Key Press時 Next Component focus
	if ( nChar == ENTER_KEY ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
	//Control Key가 눌려져 있다면 단축키 처리
	//if (bControl) fnc_HotKey(this, nChar);
}

// 우편번호 찾기
function fcPostDialoge(obj)
{
	var sFormId 	= "";
	var sArgs 		= "";
	var arrReturn   = array(2);
	var nRow		= dsCommDesc.row;

	sFormId = "common::comPost.xml";
	sArgs = "";	

	if( dsCommDesc.rowcount == 0 )
	{
		fnc_Message(SNIS_RBM_A002, "N");
		return;
	}

	arrReturn = Dialog(sFormId, sArgs, 334, 443, "");

	dsCommDesc.SetColumn(nRow, "POST_CD", replace(arrReturn[0],"-",""));
	dsCommDesc.SetColumn(nRow, "ADDR"   , arrReturn[2]);
}

// Dataset Load시
function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{	

	switch (obj.id) {
		case "dsCommDesc" :
			break;

		case "dsFloorMana" :

			var nCommAreaSqmtTotal = 0;
			var nCommAreaPy        = 0;
			var nOnlyAreaSqmt      = 0;
			var nOnlyAreaPy        = 0;

			var nTotAreaPy        = 0;
			var nTotAreaSqmt      = 0;

			for(var i = 0 ; i < dsFloorMana.GetRowCount() ; i++)
			{	

				// 공용면적 계산(m2)
				nCommAreaSqmtTotal += toFloat(dsFloorMana.GetColumn(i, "COMM_AREA_SQMT"));
				// 공용면적 계산(평)
				nCommAreaPy += toFloat(dsFloorMana.GetColumn(i, "COMM_AREA_PY"));
				// 전용면적 계산(m2)
				nOnlyAreaSqmt += toFloat(dsFloorMana.GetColumn(i, "ONLY_AREA_SQMT"));
				// 전용면적 계산(평)
				nOnlyAreaPy += toFloat(dsFloorMana.GetColumn(i, "ONLY_AREA_PY"));

				nTotAreaPy   += toFloat(dsFloorMana.GetColumn(i, "COMM_AREA_PY")) + toFloat(dsFloorMana.GetColumn(i, "ONLY_AREA_PY"));
				nTotAreaSqmt += toFloat(dsFloorMana.GetColumn(i, "COMM_AREA_SQMT")) + toFloat(dsFloorMana.GetColumn(i, "ONLY_AREA_SQMT"));
			}

			mskCommAreaSqmtTotal.Value = nCommAreaSqmtTotal;
			mskCommAreaPyTotal.Value   = nCommAreaPy;
			mskOnlyAreaSqmtTotal.Value = nOnlyAreaSqmt;
			mskOnlyAreaPyTotal.Value   = nOnlyAreaPy;

			mskTotAreaPyTotal.Value   	= nTotAreaPy;
			mskTotAreaSqmtTotal.Value   = nTotAreaSqmt;

			break;
	}
}

// 일반사항이 insert일 경우에 컬럼값의 변경이 있다면 체크해준다.
function fcDsChangeCheck() {

	var nRow = dsCommDesc.currow;

	if( dsCommDesc.GetColumn(nRow, "OP_GOAL")        	   .length > 0  ||
		dsCommDesc.GetColumn(nRow, "POST_CD")        	   .length > 0  ||
		dsCommDesc.GetColumn(nRow, "ADDR")           	   .length > 0  ||
		dsCommDesc.GetColumn(nRow, "SALES_GOAL_CRA") 	   .length > 0  ||
		dsCommDesc.GetColumn(nRow, "SALES_GOAL_MRA")       .length > 0  ||
		dsCommDesc.GetColumn(nRow, "SALES_GOAL_CRA_GREEN") .length > 0  ||
		dsCommDesc.GetColumn(nRow, "SALES_GOAL_MRA_GREEN") .length > 0  ||
		dsCommDesc.GetColumn(nRow, "INSTALL_ALOW_DT")	   .length > 0  ||
		dsCommDesc.GetColumn(nRow, "OPEN_DT")        	   .length > 0  ||
		dsCommDesc.GetColumn(nRow, "ENT_FIX_NUM")    	   .length > 0  ) {

		return false;
	}
	return true;
}
function fcEdOnChanged(obj,strText)
{
		switch ( obj.id ) {
			case "mskSalesGoalCra" :
				mskSalesTotal.value = ToFloat(mskSalesGoalCra.Value)  + ToFloat(mskSalesGoalMra.Value) ;	

				break;
			case "mskSalesGoalMra" :
				mskSalesTotal.value = ToFloat(mskSalesGoalCra.Value)  + ToFloat(mskSalesGoalMra.Value) ;
				break;

		}
}

