/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormLoadSetting(obj) {  
    sBUTTONLIST   = 'FTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

	//공통코드 조회
	fcd_GetComboDs(dsInComCd);

    // 목록 조회 
    fcSearch(); 
}


/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/

function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "dsList";
	if (!fnc_FormUnloadCheck(sDataset  , sFormId)) return false;
}


/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}
}


/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcInit(obj) {
    dsList.ClearData();
    dsUploadFile.ClearData();
}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearchCheck(obj) {
	fcInit(obj);
	return true;
}


//첨부파일 조회 전 Check
function fcSearchFileCheck() {
	dsUploadFile.ClearData();
	return true;
}


function fcSearch(obj) 
{
    if (!fcSearchCheck(obj)) return;	// 필수조건 체크 및 그리드 초기화

	var sServiceName = "rbo4010-service:searchList";
    var sInDataSet   = "";
    var sOutDataSet  = "dsList=dsList ";     

	fcd_AddParam("PARTS_LCD", cboSchPartsLcd.Value);	// 발매기
	fcd_AddParam("PARTS_MCD", cboSchPartsMcd.Value);	// 부품모델분류 

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


//첨부파일 조회
function fcSearchFile(sAttFileKey)
{	
	if ( !fcSearchFileCheck() ) return;

    var sServiceName = "common_file-service:searchFileMana";
    var sInDataSet   = "";
    var sOutDataSet  = "dsUploadFile=dsUploadFile ";

    //조회조건
    fcd_AddParam("ATT_FILE_KEY", sAttFileKey);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveUserCheck(obj) {
	// 로그인한 유저가 본장(광명) 관리자가 아니면,
 	if( gdsUser.GetColumn(0, "BRNC_CD") != "00" ) {
		// 지점권한이 없습니다 . \r\n [ @ ] 작업 할 수 없습니다.
		fnc_Message(SNIS_RBM_0007, "N", "저장");
		return;
 	}
}


function fcSaveCheck(obj) {  

 	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList) && 
         !fnc_DatasetChangeCheck(dsUploadFile)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "PARTS_LCD|PARTS_MCD|PARTS_NM|MADE_GBN|USE_YN";
	sMandatoryColName = "발매기|부품모델분류|부품명|구분|사용여부";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}
	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rbo4010-service:SavePartsModel";
	var sInDataSet   = "dsList=dsList:u dsUploadFile=dsUploadFile:u";
	var sOutDataSet  = "dsList=dsList ";

	var confirmMsg = "저장하시겠습니까?";
    if(fnc_Message(SNIS_CRA_B999, "Y", confirmMsg)) {           

		//첨부파일 서버 업로드 
		if( dsUploadFile.GetRowCount() > 0 ) {
			for (var i = 0 ; i < dsUploadFile.GetRowCount() ; i++ ) {
				if( dsUploadFile.GetRowType(i) == "insert" ) {

					var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
												, dsUploadFile.GetColumn(i, "FILE_PATH" )
												, dsUploadFile.GetColumn(i, "FILE_NM" ) 
												,"");

					var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

					if ( rtn_arr[0] != "SUCC" ) {

						fnc_Message(SNIS_COM_1050, "N", rtn_arr[1]);
						return false;
					}

					dsUploadFile.SetColumn(i, "FILE_PATH", rtn_arr[1] );
					dsUploadFile.SetColumn(i, "FILE_SIZE", fileSize );
					dsUploadFile.SetColumn(i, "REAL_FILE_NM", rtn_arr[3] );

					//Trace(dsUploadFile.savexml());

				} // end if
			}
		} // end 

		var nRow = dsList.row;
		strCurRow = nRow;

		fcd_AddParam("PARTS_CD", dsList.GetColumn(nRow, "PARTS_CD"));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}



/*-------------------------------------------------------------------------------------------------+
>>  화면 종료(닫기) 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcEnd(obj) {
	//화면 종료
	Close();	
}




/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/

function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	//조회 완료 후
	if ( sServiceName == "rbo4010-service:searchList" ) {

	}			

	//저장 완료 후
	if ( sServiceName == "rbo4010-service:SavePartsModel" ) {
		fcSearch();
	}	

	//이미지 파일(첨부파일) 조회 완료 후
	if ( sServiceName == "common_file-service:searchFileMana" ) {
		imgModel.ImageID = "";

		if( dsUploadFile.rowcount > 0 ) {
			sFilePath = GBL_SERVER_UPLOAD_DIR + SubStr(GBL_MENUID,0,3) + "/" 
			          + dsUploadFile.GetColumn(0, "REAL_FILE_NM");
			imgModel.ImageID = sFilePath;
		}
	}

}


/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcAddRow(obj) {
	// dsList ADD ROW
	if (obj.GetForm().Id == "revBtnRow") {

		var nRow;	// Record Add
		nRow = dsList.AddRow();

		cboUseYn.Index = 0;		// 사용여부	( default : 사용 )
		cboMadeGbn.Index = 0;	// 원산지구분 ( default : 국산 )


		if(Length(cboSchPartsLcd.Value) >0){
			dsList.SetColumn(nRow,"PARTS_LCD",cboSchPartsLcd.Value);
		}
		edPartsNm.SetFocus();

	// FILE UPLOAD ADD ROW
	} else if (obj.GetForm().Id == "fileBtnRow") {

		var sResponse = fcd_GetSelectFile("Open", "", "IMG");  

		if ( fnc_IsNull(sResponse) ) return;

		if( dsUploadFile.rowcount > 0 ) {
			dsUploadFile.DeleteRow(0);
		}

		var nRow = dsUploadFile.addRow();

		dsUploadFile.SetColumn(nRow, "ATT_FILE_KEY", dsList.GetColumn(dsList.row,"ATT_FILE_KEY"));
		dsUploadFile.SetColumn(nRow, "FILE_PATH"   , sResponse[0] + "\\" + trim(sResponse[1]));
		dsUploadFile.SetColumn(nRow, "FILE_NM"     , trim(sResponse[1]));

		imgModel.ImageID =  sResponse[0] + "\\" + trim(sResponse[1]); 
	}
}
