function fcSearchCheck(obj) {
	fcInit(obj);	
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbr1035-service:searchConEquip";
	var sInDataSet   = "";

	/////////////////////////////////////////////////// dsConEquip2=dsConEquip2

	var sOutDataSet  = "dsConEquip=dsConEquip ";
		sOutDataSet += "dsConEquip2=dsConEquip2 ";

	//조회조건
	//fcd_AddParam("BRNC_CD", parent.cboBrncCdSearch.Value);
	fcd_AddParam("STND_YEAR", parent.cboStndYearSearch.Value);



	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSaveCheck(obj) {  

 	// 변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsList) ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsList;
	sMandatoryColID   = "PARTS_LCD|PARTS_CD";
	sMandatoryColName = "발매기|부품코드";
	oGrid             = grdList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}

	return true;
}


function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "RBO1035-service:SavePartsIn";
	var sInDataSet   = "dsList=dsList:u ";
	var sOutDataSet  = "dsList=dsList ";

	var confirmMsg = "저장하시겠습니까?";
    if(fnc_Message(SNIS_CRA_B999, "Y", confirmMsg)) {           

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
	}
}

