function fcAddRow(obj) {

	var nRow;	// Record Add

	////////////////////////////
	switch ( obj.GetForm().id ) {
		case "revBtnRow" :
			nRow = dsConEquip.AddRow();
			grdConEquip.SetFocus(nRow);
			grdConEquip.SetCellPos(1);
			break;

		case "revBtnRow2" :
			nRow = dsConEquip2.AddRow();
			grdConEquip2.SetFocus(nRow);		// 포커스준 Row의 첫번째 Cell에 포지션을 둔다.
			grdConEquip2.SetCellPos(1);
			break;
	}

	/*

