function fcInit(obj) {
    dsEvntMana    .ClearData();
	dsEvntHistMana.ClearData();
	dsUploadFile  .ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

//사건목록 조회 전 Check
function fcSearchCheck(obj) {

    edEvntNmSearch.Text = Trim(edEvntNmSearch.Text);
	fcInit(obj);

	return true;
}

//사건목록 조회
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rbr4020-service:searchEvntMana";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEvntMana=dsEvntMana ";     

    //조회조건
	fcd_AddParam("GEN_DT_FROM", calFromDt.Value);		
	fcd_AddParam("GEN_DT_TO"  , calToDt.Value);
	fcd_AddParam("BRNC_CD"    , cboBrncCdSearch.Value);
	fcd_AddParam("EVNT_NM"    , edEvntNmSearch.Text);
	fcd_AddParam("END_YN"     , cboEndYnSearch.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

//사건이력 조회 전 Check
function fcSearchSubCheck() {
	dsEvntHistMana.ClearData();
	dsUploadFile  .ClearData();
	return true;
}

//사건이력 조회
function fcSearchSub(obj) {
	if ( !fcSearchSubCheck(obj) ) return;

	var sServiceName = "rbr4020-service:searchEvntHistMana";
    var sInDataSet   = "";
    var sOutDataSet  = "dsEvntHistMana=dsEvntHistMana ";     

    //사건에서 가져온 사건이력Key
	fcd_AddParam("BRNC_CD" , dsEvntMana.GetColumn(dsEvntMana.row, "BRNC_CD" ));
	fcd_AddParam("EVNT_SEQ", dsEvntMana.GetColumn(dsEvntMana.row, "EVNT_SEQ"));

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

//첨부파일 조회 전 Check
function fcSearchFileCheck() {
	dsUploadFile.ClearData();
	return true;
}

//첨부파일 조회
function fcSearchFile(sAttFileKey){
	if ( !fcSearchFileCheck() ) return;

	var sServiceName = "common_file-service:searchFileMana";
	var sInDataSet   = "";
	var sOutDataSet  = "dsUploadFile=dsUploadFile ";

	//사건이력에 존재하는 첨부파일키
	fcd_AddParam("ATT_FILE_KEY", sAttFileKey);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

// 저장 전에 체크
function fcSaveCheck(obj) {  
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsEvntHistMana) && 
	     !fnc_DatasetChangeCheck(dsUploadFile)) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	//필수항목체크
	oDataSet          = dsEvntHistMana;
	sMandatoryColID   = "GEN_DT|MNG|TITLE|EVNT_CONT";
	sMandatoryColName = "발생일자|담당자|제목|내용";
	oGrid             = grdEvntHistMana;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck() ) return;   

    if(fnc_Message(SNIS_COM_1012, "Y")) {           
		var sServiceName = "rbr4020-service:saveEvntHistMana";
		var sInDataSet   = "dsEvntHistMana=dsEvntHistMana:u dsUploadFile=dsUploadFile:u ";
		var sOutDataSet  = "dsEvntHistMana=dsEvntHistMana ";  	

		//첨부파일 서버 업로드 
		if( dsUploadFile.GetRowCount() > 0 ) {
			for (var i = 0 ; i < dsUploadFile.GetRowCount() ; i++ ) {
				if(dsUploadFile.GetRowType(i) == "insert" ) {
					var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
												, dsUploadFile.GetColumn(i, "FILE_PATH" )
												, dsUploadFile.GetColumn(i, "FILE_NM" ) 
												,"");

					var fileSize = divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

					if ( rtn_arr[0] != "SUCC" ) {
						fnc_Message(SNIS_COM_1050, "N", rtn_arr[1]);
						return false;
					}

					dsUploadFile.SetColumn(i, "FILE_PATH", rtn_arr[1] );
					dsUploadFile.SetColumn(i, "FILE_SIZE", fileSize );

				} // end if
			}
		} // end 

		//첨부파일 변경시, 사건이력정보 같이 넘겨줌
		fcd_AddParam("BRNC_CD" , dsEvntHistMana.GetColumn(dsEvntHistMana.currow,"BRNC_CD"));
		fcd_AddParam("EVNT_SEQ", dsEvntHistMana.GetColumn(dsEvntHistMana.currow,"EVNT_SEQ"));
		fcd_AddParam("SEQ"     , dsEvntHistMana.GetColumn(dsEvntHistMana.currow,"SEQ"));

		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }
}

