/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {

	switch ( obj.GetForm().id ) {
		case "evntHistBtnRow" :	// 사건이력 추가

			//DataSet의 Header 정보가 있을 때  Add 처리
			if ( fnc_DatasetChangeCheck(dsEvntHistMana) ) {
				fnc_Message(SNIS_COM_1051, "N");
				return;
			}

			var nRowM = dsEvntMana.row;

			//사건목록이 없을 경우, 사건이력 추가 불가능
			if ( nRowM < 0 ) {
				fnc_Message(SNIS_RBM_A003, "N", "사건목록", "사건이력");
				return;
			}

			nRow  = dsEvntHistMana.AddRow();

			//자동 Setting
			dsEvntHistMana.SetColumn(nRow, "BRNC_CD" , dsEvntMana.GetColumn(nRowM, "BRNC_CD" ));
			dsEvntHistMana.SetColumn(nRow, "EVNT_SEQ", dsEvntMana.GetColumn(nRowM, "EVNT_SEQ"));
			dsEvntHistMana.SetColumn(nRow, "GEN_DT"  , sCurDate);
			dsEvntHistMana.SetColumn(nRow, "MNG"     , gdsUser.GetColumn(gdsUser.row, "USER_NM"));

			calGenDt.SetFocus(); // 입력 첫 항목으로 Focus

			break;

		case "fileBtnRow" :	// 첨부파일 추가
			var sResponse = fcd_GetSelectFile();

			if ( fnc_IsNull(sResponse) ) {
				return;
			}

			var nRow = dsUploadFile.addRow();
			dsUploadFile.SetColumn(nRow, "ATT_FILE_KEY" , dsEvntHistMana.GetColumn(dsEvntHistMana.row,"ATT_FILE_KEY"));
			dsUploadFile.SetColumn(nRow, "FILE_PATH", sResponse[0] + "\\" + sResponse[1]);
			dsUploadFile.SetColumn(nRow, "FILE_NM"  , sResponse[1]);

			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	switch ( obj.GetForm().id ) {
		case "evntHistBtnRow" :
			if ( !fnc_DeleteCheck(dsEvntHistMana, "CHK", "1") ) return;
			fcSearchFile(dsEvntHistMana.GetColumn(dsEvntHistMana.currow, "ATT_FILE_KEY"));
			break;

		case "fileBtnRow" :
			if ( !fnc_DeleteCheck(dsUploadFile, "CHK", "1") ) return;
			break;
	}
}

