function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 사건 목록 조회 완료 후
	if ( sServiceName == "rbr4020-service:searchEvntMana" ) {

	}			

	// 사건 이력 조회 완료 후
	if ( sServiceName == "rbr4020-service:searchEvntHistMana" ) {

	}

	// 첨부 파일 조회 완료 후
	if ( sServiceName == "rbr4020-service:searchFileMana" ) {

	}

	// 저장 완료 후
	if ( sServiceName == "rbr4020-service:saveEvntHistMana" ) {
		fcSearchSub();		
	}	
}

